INSERT INTO `post` (`id`, `title`, `content`, `abstract`, `premium`, `status`, `postdate`, `reason`, `views`, `writeby`, `cid`) VALUES
(1,	'Đà Nẵng cách ly 7 trên 8 quận, huyện',	'<p style=\"text-align: justify;\"><span style=\"font-size: 18pt;\"><strong>Từ 13h h&ocirc;m nay, Đ&agrave; Nẵng c&aacute;ch ly x&atilde; hội th&ecirc;m huyện H&ograve;a Vang. Như vậy, 7 trong số 8 quận, huyện của th&agrave;nh phố n&agrave;y sẽ c&aacute;ch ly theo Chỉ thị 16.</strong></span></p>\r\n<p style=\"text-align: center;\"><strong><iframe src=\"https://www.youtube.com/embed/nT2bqIBv1RM\" width=\"811\" height=\"456\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></strong></p>\r\n<p style=\"text-align: justify;\">Người Đ&agrave; Nẵng: \'Ch&uacute;ng t&ocirc;i b&igrave;nh tĩnh, kh&ocirc;ng lo lắng\' Từ 0h ng&agrave;y 28/7, TP Đ&agrave; Nẵng thực hiện c&aacute;ch ly x&atilde; hội ở 6 quận để ngăn dịch Covid-19. Người d&acirc;n cho biết họ đều b&igrave;nh tĩnh v&agrave; thực hiện theo hướng dẫn của ch&iacute;nh quyền địa phương.</p>\r\n<p style=\"text-align: justify;\">S&aacute;ng 28/8, &ocirc;ng L&ecirc; Trung Chinh, Ph&oacute; chủ tịch UBND TP Đ&agrave; Nẵng, cho biết từ 13h h&ocirc;m nay, địa phương sẽ thực hiện c&aacute;ch ly x&atilde; hội to&agrave;n th&agrave;nh phố.</p>\r\n<p style=\"text-align: justify;\">Sau &aacute;p dụng c&aacute;ch ly với 6 quận, chiều nay c&oacute; th&ecirc;m huyện H&ograve;a Vang thực hiện c&aacute;c biện ph&aacute;p cấp b&aacute;ch ph&ograve;ng, chống dịch Covid-19 theo Chỉ thị số 16 của Thủ tướng. Huyện đảo Ho&agrave;ng Sa l&agrave; địa b&agrave;n c&ograve;n lại của Đ&agrave; Nẵng chưa c&aacute;ch ly x&atilde; hội.</p>\r\n<p style=\"text-align: justify;\">Trong quyết định vừa ban h&agrave;nh, l&atilde;nh đạo th&agrave;nh phố giao UBND huyện H&ograve;a Vang chủ tr&igrave;, phối hợp với lực lượng c&ocirc;ng an gi&aacute;m s&aacute;t, chịu tr&aacute;ch nhiệm về việc thực hiện nghi&ecirc;m t&uacute;c gi&atilde;n c&aacute;ch x&atilde; hội tr&ecirc;n địa b&agrave;n.</p>\r\n<p style=\"text-align: justify;\">Địa phương cần tăng cường c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, thực hiện nghi&ecirc;m việc c&aacute;ch ly giữa c&aacute;c gia đ&igrave;nh, tổ d&acirc;n phố, phường, quận v&agrave; xử l&yacute; nghi&ecirc;m c&aacute;c trường hợp vi phạm.</p>\r\n<p style=\"text-align: justify;\">Những người ở ph&acirc;n xưởng, nh&agrave; m&aacute;y sản xuất phải bảo đảm khoảng c&aacute;ch an to&agrave;n, đeo khẩu trang, thực hiện khử tr&ugrave;ng, diệt khuẩn theo quy định.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://znews-photo.zadn.vn/w660/Uploaded/jugtzb/2020_07_28/Giam_zing_14_1.jpg\" alt=\"\" width=\"660\" height=\"440\" /></p>\r\n<p style=\"text-align: justify;\">Chiều h&ocirc;m qua (27/7), Đ&agrave; Nẵng đ&atilde; thực hiện c&aacute;ch ly x&atilde; hội tại c&aacute;c quận Hải Ch&acirc;u, Thanh Kh&ecirc;, Sơn Tr&agrave;, Ngũ H&agrave;nh Sơn, Cẩm Lệ, Li&ecirc;n Chiểu.</p>\r\n<p style=\"text-align: justify;\">Theo chỉ thị 16, người d&acirc;n chỉ ra ngo&agrave;i trong trường hợp thật sự cần thiết như mua lương thực, thực phẩm, thuốc men, cấp cứu, l&agrave;m việc tại nh&agrave; m&aacute;y, cơ sở sản xuất, cơ sở kinh doanh dịch vụ, h&agrave;ng h&oacute;a thiết yếu.</p>\r\n<p style=\"text-align: justify;\">Khi ra đường, mọi người phải giữ khoảng c&aacute;ch tối thiểu 2 m, kh&ocirc;ng tập trung qu&aacute; 2 người ngo&agrave;i phạm vi c&ocirc;ng sở, trường học, bệnh viện v&agrave; nơi c&ocirc;ng cộng.</p>\r\n<p style=\"text-align: justify;\">Li&ecirc;n quan hoạt động vận tải, TP Đ&agrave; Nẵng y&ecirc;u cầu Sở GTVT chỉ đạo c&aacute;c đơn vị li&ecirc;n quan tạm dừng hoạt động vận tải h&agrave;nh kh&aacute;ch tuyến cố định qua c&aacute;c bến xe kh&aacute;ch.</p>\r\n<p style=\"text-align: justify;\">Ri&ecirc;ng hoạt động kinh doanh taxi, xe hợp đồng, du lịch v&agrave; c&aacute;c phương tiện vận tải thủy nội tr&ecirc;n địa b&agrave;n sẽ dừng hoạt động ho&agrave;n to&agrave;n trong 15 ng&agrave;y.</p>\r\n<p style=\"text-align: justify;\">Trong ba ng&agrave;y từ 25 đến 27/7, Việt Nam ghi nhận 15 ca nhiễm nCoV cộng đồng (Đ&agrave; Nẵng 14 ca, Quảng Ng&atilde;i 1 ca). Tổng ca bệnh nhiễm l&ecirc;n 431, trong đ&oacute; 365 người đ&atilde; khỏi.</p>\r\n<p style=\"text-align: justify;\">Ng&agrave;nh y tế Đ&agrave; Nẵng đ&atilde; khoanh v&ugrave;ng khoảng 12.000 người tiếp x&uacute;c v&agrave; tiếp x&uacute;c gần với c&aacute;c bệnh nh&acirc;n để x&eacute;t nghiệm.</p>',	'Từ 13h hôm nay, Đà Nẵng cách ly xã hội thêm huyện Hòa Vang. Như vậy, 7 trong số 8 quận, huyện của thành phố này sẽ cách ly theo Chỉ thị 16.',	1,	2,	'2020-08-17 00:00:20',	NULL,	1009,	3,	1),
(2,	'Tổng bí thư: \'Không để cán bộ vi phạm cơ cấu vào cấp ủy\'',	'<p style=\"text-align: justify;\"><span style=\"font-size: 18pt;\"><strong>Nhắc lực lượng c&ocirc;ng an phải l&agrave;m thật tốt vai tr&ograve;, tr&aacute;ch nhiệm trong c&ocirc;ng t&aacute;c bảo vệ ch&iacute;nh trị nội bộ, Tổng b&iacute; thư lưu &yacute; kh&ocirc;ng để những c&aacute;n bộ, đảng vi&ecirc;n vi phạm cơ cấu v&agrave;o cấp ủy.</strong></span></p>\r\n<p style=\"text-align: justify;\">S&aacute;ng 22/6, Tổng b&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c c&ugrave;ng dự Hội nghị Ban Thường vụ Đảng ủy C&ocirc;ng an Trung ương 6 th&aacute;ng đầu năm 2020.</p>\r\n<p style=\"text-align: justify;\">Ph&aacute;t biểu chỉ đạo tại Hội nghị, Tổng b&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng nhắc đến dự thảo B&aacute;o c&aacute;o Ch&iacute;nh trị tr&igrave;nh Đại hội Đảng bộ C&ocirc;ng an Trung ương lần thứ VII, nhiệm kỳ 2020-2025.</p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>L&agrave;m tốt c&ocirc;ng t&aacute;c bảo vệ ch&iacute;nh trị nội bộ</strong></span></p>\r\n<p style=\"text-align: justify;\">Theo Tổng b&iacute; thư, đ&acirc;y l&agrave; văn kiện trung t&acirc;m của Đại hội, l&agrave; sự kết tinh tr&iacute; tuệ, t&acirc;m huyết, tr&aacute;ch nhiệm, sự s&aacute;ng tạo, tầm nh&igrave;n v&agrave; đề ra phương hướng, nhiệm vụ của Đảng bộ C&ocirc;ng an Trung ương v&agrave; cả lực lượng CAND trong nhiệm kỳ tới.</p>\r\n<p style=\"text-align: justify;\">Do đ&oacute;, dự thảo b&aacute;o c&aacute;o n&agrave;y rất quan trọng, mang t&iacute;nh chiến lược, Đảng bộ C&ocirc;ng an Trung ương phải khẳng định cho được th&agrave;nh tựu rất to lớn của c&ocirc;ng t&aacute;c giữ g&igrave;n an ninh ch&iacute;nh trị, trật tự an to&agrave;n x&atilde; hội.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://znews-photo.zadn.vn/w1024/Uploaded/ovhpaob/2020_06_22/bca_0654.jpg\" alt=\"\" width=\"627\" height=\"450\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 8pt;\">Tổng b&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng gặp c&aacute;c l&atilde;nh đạo Bộ C&ocirc;ng an. Ảnh: mps.gov.vn.</span></p>\r\n<p style=\"text-align: justify;\">Tổng b&iacute; thư lưu &yacute; n&ecirc;n nghi&ecirc;n cứu bổ sung, ho&agrave;n thiện cơ chế, ch&iacute;nh s&aacute;ch, ph&aacute;p luật về bảo vệ an ninh quốc gia, đấu tranh ph&ograve;ng, chống tội phạm, vi phạm ph&aacute;p luật, bảo đảm trật tự an to&agrave;n x&atilde; hội v&agrave; về hoạt động của lực lượng CAND đ&aacute;p ứng kịp thời đ&ograve;i hỏi của t&igrave;nh h&igrave;nh thực tiễn; nhất l&agrave; những vấn đề mới, những vấn đề kh&oacute;, th&aacute;ch thức an ninh phi truyền thống.</p>\r\n<p style=\"text-align: justify;\">Để ho&agrave;n thiện Văn kiện - B&aacute;o c&aacute;o Ch&iacute;nh trị, Tổng b&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng đề nghị Đảng ủy C&ocirc;ng an Trung ương, Bộ C&ocirc;ng an cần tiếp tục tổ chức lấy &yacute; kiến đ&oacute;ng g&oacute;p của c&aacute;c cấp ủy, tổ chức đảng trực thuộc, tranh thủ &yacute; kiến của c&aacute;n bộ C&ocirc;ng an l&atilde;o th&agrave;nh, c&aacute;c nh&agrave; khoa học, chuy&ecirc;n gia trong CAND.</p>\r\n<p style=\"text-align: justify;\">Tập trung thời gian, nh&acirc;n lực để ho&agrave;n thiện v&agrave; chuẩn bị thật tốt c&aacute;c điều kiện để b&aacute;o c&aacute;o tập thể Bộ Ch&iacute;nh trị cho &yacute; kiến về c&ocirc;ng t&aacute;c chuẩn bị văn kiện, nh&acirc;n sự tr&igrave;nh Đại hội đại biểu Đảng bộ C&ocirc;ng an Trung ương lần thứ VII, nhiệm kỳ 2020-2025.</p>\r\n<p style=\"text-align: justify;\">Theo người đứng đầu Đảng, Nh&agrave; nước, Bộ Ch&iacute;nh trị đ&atilde; c&oacute; kết luận về tiếp tục tăng cường c&ocirc;ng t&aacute;c bảo vệ ch&iacute;nh trị nội bộ trong t&igrave;nh h&igrave;nh hiện nay, trong đ&oacute; việc nắm v&agrave; giải quyết c&aacute;c vấn đề ch&iacute;nh trị l&agrave; một nhiệm vụ quan trọng cần phải tập trung thực hiện tốt, đặc biệt l&agrave; chuẩn bị nh&acirc;n sự Đại hội đảng bộ c&aacute;c cấp, tiến tới Đại hội XIII của Đảng.</p>\r\n<p style=\"text-align: justify;\">Do vậy, lực lượng CAND phải l&agrave;m thật tốt vai tr&ograve;, tr&aacute;ch nhiệm của m&igrave;nh trong c&ocirc;ng t&aacute;c bảo vệ ch&iacute;nh trị nội bộ, kh&ocirc;ng để những c&aacute;n bộ, đảng vi&ecirc;n vi phạm ti&ecirc;u chuẩn ch&iacute;nh trị, nguy&ecirc;n tắc, điều lệ Đảng, vi phạm ph&aacute;p luật c&oacute; thể cơ cấu v&agrave;o cấp ủy hoặc đi dự Đại hội cấp tr&ecirc;n...</p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>Đảm bảo tuyệt đối an ninh, an to&agrave;n cho Đại hội Đảng</strong></span></p>\r\n<p style=\"text-align: justify;\">Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c ghi nhận Đảng bộ C&ocirc;ng an Trung ương l&agrave; Đảng bộ mẫu mực, l&atilde;nh đạo to&agrave;n diện c&aacute;c mặt c&ocirc;ng t&aacute;c c&ocirc;ng an. &Ocirc;ng cũng đ&aacute;nh gi&aacute; cao vai tr&ograve; v&agrave; những đ&oacute;ng g&oacute;p rất quan trọng của lực lượng C&ocirc;ng an nh&acirc;n d&acirc;n trong c&ocirc;ng t&aacute;c ph&ograve;ng, chống, đẩy l&ugrave;i dịch Covid-19 vừa qua.</p>\r\n<p style=\"text-align: justify;\">Chỉ đạo một số mặt c&ocirc;ng t&aacute;c trọng t&acirc;m thời gian tới, Thủ tướng nhấn mạnh bất luận trong mọi điều kiện, ho&agrave;n cảnh n&agrave;o, lực lượng CAND cũng phải tuyệt đối trung th&agrave;nh với Đảng, Nh&agrave; nước v&agrave; nh&acirc;n d&acirc;n.</p>\r\n<p style=\"text-align: justify;\">&ldquo;Mọi hoạt động của lực lượng c&ocirc;ng an l&agrave; phục vụ nh&acirc;n d&acirc;n, phụng sự Tổ quốc, phục vụ c&oacute; hiệu quả nhiệm vụ ph&aacute;t triển kinh tế, x&atilde; hội, đối ngoại của đất nước. Lực lượng c&ocirc;ng an phải bảo đảm tuyệt đối an ninh, an to&agrave;n c&aacute;c mục ti&ecirc;u, c&ocirc;ng tr&igrave;nh trọng điểm, c&aacute;c sự kiện ch&iacute;nh trị, văn h&oacute;a, x&atilde; hội quan trọng của đất nước&rdquo;, Thủ tướng n&oacute;i.</p>\r\n<p style=\"text-align: justify;\">&Ocirc;ng nhấn mạnh nhiệm vụ trọng t&acirc;m tới đ&acirc;y l&agrave; bảo vệ tuyệt đối an ninh, an to&agrave;n Đại hội đảng bộ c&aacute;c cấp, tiến tới Đại hội đại biểu to&agrave;n quốc lần thứ XIII của Đảng, g&oacute;p phần v&agrave;o th&agrave;nh c&ocirc;ng Đại hội lần thứ XIII của Đảng...</p>\r\n<p style=\"text-align: justify;\">B&ecirc;n cạnh đ&oacute;, người đứng đầu Ch&iacute;nh phủ cho rằng lực lượng C&ocirc;ng an nh&acirc;n d&acirc;n phải đặc biệt coi trọng c&ocirc;ng t&aacute;c x&acirc;y dựng Đảng, x&acirc;y dựng lực lượng trong sạch, vững mạnh, c&oacute; sức chiến đấu cao, thực sự l&agrave; chỗ dựa tin cậy của Đảng, Nh&agrave; nước v&agrave; nh&acirc;n d&acirc;n.</p>\r\n<p style=\"text-align: justify;\">Đồng thời, ki&ecirc;n quyết đấu tranh ph&ograve;ng, chống c&aacute;c biểu hiện suy tho&aacute;i về tư tưởng ch&iacute;nh trị, đạo đức, lối sống, &ldquo;tự diễn biến&rdquo;, &ldquo;tự chuyển h&oacute;a&rdquo;, nhũng nhiễu, xa rời, v&ocirc; cảm trước nh&acirc;n d&acirc;n. C&oacute; giải ph&aacute;p cụ thể nhằm n&acirc;ng cao năng lực, hoạt động của lực lượng c&ocirc;ng an x&atilde;, phường, thị trấn đ&aacute;p ứng y&ecirc;u cầu, nhiệm vụ bảo đảm an ninh trật tự tại cơ sở.</p>\r\n<p style=\"text-align: justify;\">Tiếp thu c&aacute;c &yacute; kiến, Bộ trưởng C&ocirc;ng an T&ocirc; L&acirc;m khẳng định sẽ cụ thể h&oacute;a trong c&ocirc;ng t&aacute;c l&atilde;nh đạo, chỉ đạo, điều h&agrave;nh c&aacute;c mặt c&ocirc;ng t&aacute;c của lực lượng c&ocirc;ng an; trước mắt l&agrave; bổ sung, ho&agrave;n chỉnh c&aacute;c văn bản trước khi đưa ra Hội nghị sơ kết c&ocirc;ng t&aacute;c c&ocirc;ng an 6 th&aacute;ng đầu năm 2020 để qu&aacute;n triệt thực hiện tới to&agrave;n thể c&aacute;c c&aacute;n bộ trong cấp ủy, l&atilde;nh đạo C&ocirc;ng an c&aacute;c cấp trong CAND thời gian tới.</p>\r\n<p style=\"text-align: justify;\">Bộ trưởng T&ocirc; L&acirc;m đề nghị c&aacute;c ủy vi&ecirc;n Đảng ủy C&ocirc;ng an Trung ương nhận thức s&acirc;u sắc, đầy đủ tr&aacute;ch nhiệm của m&igrave;nh, tự gi&aacute;c, gương mẫu thực hiện Nghị quyết, n&oacute;i đi đ&ocirc;i với l&agrave;m, quyết t&acirc;m, quyết liệt chỉ đạo qu&aacute;n triệt thực hiện hiệu quả nhiệm vụ ch&iacute;nh trị trong đảng bộ, đơn vị m&igrave;nh.</p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 18pt;\">62/63 địa phương bố tr&iacute; c&ocirc;ng an ch&iacute;nh quy về x&atilde;, thị trấn</span></strong></p>\r\n<p style=\"text-align: justify;\">Trong b&aacute;o c&aacute;o đ&aacute;nh gi&aacute; kiểm điểm c&aacute;c mặt c&ocirc;ng t&aacute;c c&ocirc;ng an 6 th&aacute;ng đầu năm 2020, Bộ C&ocirc;ng an khẳng định trước bối cảnh t&igrave;nh h&igrave;nh dịch bệnh Covid-19, lực lượng CAND đ&atilde; thể hiện r&otilde; vai tr&ograve; nơi tuyến đầu ph&ograve;ng, chống dịch bệnh, g&oacute;p phần kh&ocirc;ng nhỏ trong việc khống chế, ngăn chặn, chiến thắng dịch bệnh.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://znews-photo.zadn.vn/w1024/Uploaded/ovhpaob/2020_06_22/bca_0849.jpg\" alt=\"\" width=\"680\" height=\"447\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 8pt;\">Tổng b&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c v&agrave; Bộ trưởng C&ocirc;ng an T&ocirc; L&acirc;m tại hội nghị. Ảnh: mps.gov.vn.</span></p>\r\n<p style=\"text-align: justify;\">Song song với đ&oacute;, lực lượng CAND đ&atilde; triển khai quyết liệt, đồng bộ c&aacute;c biện ph&aacute;p ph&ograve;ng ngừa x&atilde; hội, ph&ograve;ng ngừa nghiệp vụ, chủ động dự b&aacute;o, ph&acirc;n t&iacute;ch, đ&aacute;nh gi&aacute; t&igrave;nh h&igrave;nh tội phạm, tổ chức nhiều đợt cao điểm tấn c&ocirc;ng, trấn &aacute;p tội phạm. C&ocirc;ng t&aacute;c ph&ograve;ng, chống tội phạm đạt v&agrave; vượt c&aacute;c chỉ ti&ecirc;u đề ra, điều tra, kh&aacute;m ph&aacute; nhanh c&aacute;c vụ trọng &aacute;n, kiềm chế sự gia tăng của tội phạm.</p>\r\n<p style=\"text-align: justify;\">Trong sự nghiệp đấu tranh bảo vệ an ninh quốc gia, giữ g&igrave;n trật tự an to&agrave;n x&atilde; hội đ&atilde; c&oacute; nhiều tấm gương c&aacute;n bộ, chiến sĩ c&ocirc;ng an tận tụy, dũng cảm chiến đấu, hy sinh qu&ecirc;n m&igrave;nh trong đấu tranh ph&ograve;ng, chống tội phạm, v&igrave; cuộc sống b&igrave;nh y&ecirc;n v&agrave; hạnh ph&uacute;c của nh&acirc;n d&acirc;n...</p>\r\n<p style=\"text-align: justify;\">Nổi bật trong 6 th&aacute;ng đầu năm, việc triển khai c&ocirc;ng an x&atilde; ch&iacute;nh quy cơ bản đ&atilde; ho&agrave;n th&agrave;nh với 62/63 tỉnh, th&agrave;nh phố tiến h&agrave;nh bố tr&iacute; 100% c&ocirc;ng an ch&iacute;nh quy đảm nhiệm c&aacute;c chức danh c&ocirc;ng an x&atilde;, thị trấn. Việc n&agrave;y cũng đem lại hiệu quả bước đầu khi t&igrave;nh h&igrave;nh an ninh trật tự ở địa b&agrave;n cơ sở được bảo đảm v&agrave; c&oacute; nhiều chuyển biến r&otilde; rệt.</p>',	'Nhắc lực lượng công an phải làm thật tốt vai trò, trách nhiệm trong công tác bảo vệ chính trị nội bộ, Tổng bí thư lưu ý không để những cán bộ, đảng viên vi phạm cơ cấu vào cấp ủy.',	0,	2,	'2020-08-17 00:00:16',	NULL,	8,	3,	2),
(3,	'20.000 tài xế vi phạm nồng độ cồn trong 1 tháng',	'<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>Cục CSGT (Bộ C&ocirc;ng an) cho biết sau 1 th&aacute;ng ra qu&acirc;n tổng kiểm so&aacute;t phương tiện giao th&ocirc;ng đường bộ (15/5-14/6), CSGT cả nước xử l&yacute; hơn 400.000 trường hợp vi phạm, tước hơn 27.000 bằng l&aacute;i, tạm giữ tr&ecirc;n 61.000 xe cộ.</strong></span></p>\n<p style=\"text-align: center;\"><iframe src=\"https://www.youtube.com/embed/zorfnCJsqHQ\" width=\"811\" height=\"456\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<p>So với thời gian liền kề trước khi thực hiện kế hoạch, số vi phạm tăng 73.379 lượt. Trong đ&oacute;, vi phạm chủ yếu l&agrave; kh&ocirc;ng đội mũ bảo hiểm, đi v&agrave;o đường cấm, vi phạm tốc độ. Đặc biệt, c&oacute; 20.120 t&agrave;i xế vi phạm nồng độ cồn. Ngo&agrave;i ra, cơ quan chức năng ph&aacute;t hiện 276 t&agrave;i xế xe container, &ocirc;t&ocirc; kh&aacute;ch, xe m&aacute;y vi phạm li&ecirc;n quan tới ma t&uacute;y.</p>\n<p>Về trật tự an to&agrave;n giao th&ocirc;ng, trong 6 th&aacute;ng đầu năm 2020, cả nước xảy ra 6.781 vụ tai nạn, l&agrave;m 3.235 người chết, 4.939 người bị thương. So với c&ugrave;ng kỳ, tai nạn giao th&ocirc;ng giảm 19%, số người chết giảm 15% v&agrave; số người bị thương giảm 22%.</p>\n<p style=\"text-align: center;\"><img title=\"Bai1_1.jpg\" src=\"/public/images/post_3_1.jpg\" alt=\"\" width=\"660\" height=\"439\" /></p>\n<p>CSGT kiểm tra nồng độ cồn t&agrave;i xế. Trong 1 th&aacute;ng qua đ&atilde; c&oacute; hơn 20.000 người vi phạm li&ecirc;n quan đến nồng độ cồn bị xử phạt. Ảnh: H.Q.</p>\n<p>Trung tướng Vũ Đỗ Anh Dũng, Cục trưởng Cục CSGT, đ&aacute;nh gi&aacute; việc thực hiện tổng kiểm so&aacute;t phương tiện c&oacute; thể coi l&agrave; một c&uacute; h&iacute;ch đối với việc n&acirc;ng cao &yacute; thức chấp h&agrave;nh ph&aacute;p luật của người d&acirc;n khi tham gia giao th&ocirc;ng, bởi sau thời gian gi&atilde;n c&aacute;ch x&atilde; hội, mọi người c&oacute; xu hướng &ldquo;thả lỏng&rdquo; khiến t&igrave;nh h&igrave;nh c&oacute; những diễn biến phức tạp.</p>\n<p>Theo l&atilde;nh đạo Cục CSGT, trong 1 th&aacute;ng tổng kiểm so&aacute;t, CSGT cả nước đ&atilde; sử dụng nhiều biện ph&aacute;p nghiệp vụ để ph&aacute;t hiện vi phạm, tuy nhi&ecirc;n, một số h&agrave;nh vi vi phạm chỉ được ph&aacute;t hiện khi dừng phương tiện để kiểm tra như: Vi phạm về nồng độ cồn; ma t&uacute;y; giấy ph&eacute;p l&aacute;i xe, biển số xe v&agrave; đăng k&yacute; xe giả...</p>\n<p style=\"text-align: center;\"><img title=\"Bai1_2.jpg\" src=\"/public/images/post_3_2.jpg\" alt=\"\" width=\"660\" height=\"439\" /></p>\n<p>CSGT Tuy&ecirc;n Quang kiểm tra giấy tờ xe của người d&acirc;n. Ảnh: H.Q.</p>\n<p>Đối với thời gian kiểm so&aacute;t phương tiện, &ocirc;ng đ&aacute;nh gi&aacute; CSGT thực hiện nhanh ch&oacute;ng, kh&ocirc;ng để ảnh hưởng đến đi lại của người d&acirc;n; đảm bảo an to&agrave;n cho người được kiểm tra, người tham gia giao th&ocirc;ng v&agrave; ch&iacute;nh lực lượng CSGT đang l&agrave;m nhiệm vụ.</p>\n<p>&ldquo;Đơn cử như việc kiểm so&aacute;t nồng độ cồn, CSGT hướng dẫn l&aacute;i xe điều khiển xe v&agrave;o lề đường hoặc l&agrave;n ri&ecirc;ng để kiểm tra, người l&aacute;i xe gần như kh&ocirc;ng phải xuống phương tiện, nếu kh&ocirc;ng vi phạm th&igrave; CSGT n&oacute;i lời cảm ơn về sự hợp t&aacute;c v&agrave; người l&aacute;i xe tiếp tục di chuyển. Qu&aacute; tr&igrave;nh kiểm tra kh&ocirc;ng qu&aacute; 2 ph&uacute;t. Đối với những trường hợp c&oacute; biểu hiện bất thường CSGT mới kiểm so&aacute;t giấy tờ. Đến nay, ch&uacute;ng t&ocirc;i chưa ghi nhận được trường hợp n&agrave;o kh&ocirc;ng vi phạm m&agrave; phản đối, kh&ocirc;ng chấp h&agrave;nh hiệu lệnh của CSGT&rdquo;, Cục trưởng Cục CSGT n&oacute;i.</p>\n<p>Thời gian tới, l&atilde;nh đạo Cục CSGT cho biết sẽ căn cứ t&igrave;nh h&igrave;nh thực tiễn của từng địa phương, từng thời điểm để chỉ đạo mở c&aacute;c đợt cao điểm hoặc xử l&yacute; theo c&aacute;c chuy&ecirc;n đề ph&ugrave; hợp.</p>\n<p>L&aacute;i xe kh&ocirc;ng mũ bảo hiểm quay đầu xe khi thấy chốt đo nồng độ cồn Tối 10/6, C&ocirc;ng an TP Tam Kỳ (Quảng Nam) tổng kiểm so&aacute;t phương tiện ở nhiều tuyến đường. Ph&aacute;t hiện chốt đo nồng độ cồn, nhiều t&agrave;i xế kh&ocirc;ng đội mũ bảo hiểm quay đầu xe bỏ chạy.</p>',	'Trong số hơn 400.000 tài xế vi phạm, 20.120 người bị phạt lỗi nồng độ cồn trong 1 tháng tổng kiểm soát phương tiện giao thông đường bộ.',	1,	2,	'2020-07-28 14:48:35',	NULL,	2,	3,	4),
(4,	'Vì sao TP Hà Giang ngập trong biển nước?',	'<p style=\"text-align: center;\"><iframe src=\"https://www.youtube.com/embed/3LC5lAY7H8Y\" width=\"791\" height=\"443\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>Cảnh đồi sạt, lũ cuồn cuộn đổ về H&agrave; Giang Mưa lớn v&agrave;o rạng s&aacute;ng 21/7 l&agrave;m nhiều tuyến đường tại TP H&agrave; Giang ngập s&acirc;u, h&agrave;ng trăm ng&ocirc;i nh&agrave; bị nước tr&agrave;n v&agrave;o. Tại huyện Ho&agrave;ng Su Ph&igrave;, vụ sạt lở đất xảy ra l&agrave;m 2 mẹ con tử vong.</strong></span></p>\r\n<p>Mưa lớn g&acirc;y ngập lụt nghi&ecirc;m trọng tại TP H&agrave; Giang diễn ra trong bối cảnh thi&ecirc;n tai tại Trung Quốc khiến nhiều người lo ngại.</p>\r\n<p>Theo th&ocirc;ng tin tham khảo từ Trung t&acirc;m dự b&aacute;o Kh&iacute; tượng Thủy văn Quốc gia, mưa lớn ở H&agrave; Giang v&agrave; c&aacute;c tỉnh T&acirc;y Bắc Bộ l&agrave; mưa tại chỗ, kh&ocirc;ng phải do m&acirc;y từ nơi kh&aacute;c k&eacute;o tới.</p>\r\n<p>H&agrave; Giang ngập do địa h&igrave;nh l&ograve;ng chảo</p>\r\n<p>Trao đổi với Zing, &ocirc;ng Nguyễn Đức Vinh, Gi&aacute;m đốc Sở N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển n&ocirc;ng th&ocirc;n tỉnh H&agrave; Giang, cho biết nguy&ecirc;n nh&acirc;n ch&iacute;nh khiến H&agrave; Giang ngập nghi&ecirc;m trọng trong s&aacute;ng 21/7 l&agrave; lượng mưa tr&uacute;t xuống qu&aacute; lớn.</p>\r\n<p>\"Mưa chỉ v&agrave;i giờ m&agrave; đ&atilde; hơn 100 mm l&agrave; rất lớn. Trong khi đ&oacute;, H&agrave; Giang c&oacute; địa h&igrave;nh l&ograve;ng chảo, mưa xuống nhanh nhưng nước kh&ocirc;ng kịp tho&aacute;t g&acirc;y ra ngập &uacute;ng nghi&ecirc;m trọng. Từ năm 1986 đến nay, việc ngập &uacute;ng vẫn thỉnh thoảng diễn ra\", &ocirc;ng Vinh cho biết.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_4_1.jpg\" /></p>\r\n<p>Gi&aacute;m đốc Sở NN&amp;PTNT H&agrave; Giang cho biết trận ngập tại th&agrave;nh phố kh&ocirc;ng phải lần đầu ti&ecirc;n. Ảnh: H.H.</p>\r\n<p>Đồng quan điểm, &ocirc;ng B&ugrave;i Đức Tuấn, Gi&aacute;m đốc Đ&agrave;i Kh&iacute; tượng Thủy văn khu vực Việt Bắc, cho biết th&ocirc;ng thường, những đợt lũ xảy ra thường l&agrave; lũ thượng nguồn v&agrave; xuất hiện tr&ecirc;n diện rộng. Tuy nhi&ecirc;n, đợt lần n&agrave;y lại chỉ tập trung ở H&agrave; Giang.</p>\r\n<p>H&agrave; Giang c&oacute; địa h&igrave;nh gần như một thung lũng. Xung quanh th&agrave;nh phố l&agrave; c&aacute;c r&atilde;nh n&uacute;i cao n&ecirc;n lượng nước dồn về rất nhanh.</p>\r\n<p>\"Lũ chỉ tr&ecirc;n mức b&aacute;o động 2 nhưng tập trung cục bộ ở một v&agrave;i điểm n&ecirc;n g&acirc;y ngập nhiều. Sau đ&oacute;, lượng nước ứ đọng tại c&aacute;c điểm tr&agrave;n ra g&acirc;y ngập hết cả th&agrave;nh phố\", &ocirc;ng Tuấn ph&acirc;n t&iacute;ch.</p>\r\n<p>Trong văn bản gửi c&aacute;c địa phương y&ecirc;u cầu ứng ph&oacute; với ngập lụt, lũ qu&eacute;t, &ocirc;ng Nguyễn Văn Sơn, Chủ tịch UBND tỉnh H&agrave; Giang, cũng nhận định nguy&ecirc;n nh&acirc;n g&acirc;y ngập lần n&agrave;y l&agrave; mưa lớn k&eacute;o d&agrave;i. Hệ thống tho&aacute;t nước của th&agrave;nh phố đ&atilde; cũ, xuống cấp v&agrave; kh&ocirc;ng đ&aacute;p ứng được mức độ đ&ocirc; thị h&oacute;a.</p>\r\n<p>Lượng nước kh&ocirc;ng kịp tho&aacute;t g&acirc;y ngập lụt nặng nề tại TP H&agrave; Giang. Theo b&aacute;o c&aacute;o nhanh của Chi cục Thủy lợi, khu vực trường Nội tr&uacute; của tỉnh ngập cao tới 1,2 m. Nhiều con ng&otilde; nhỏ cũng hứng chịu độ ngập tương tự do địa h&igrave;nh trũng s&acirc;u v&agrave; kh&oacute; tho&aacute;t nước.</p>\r\n<p>Kh&ocirc;ng li&ecirc;n quan đến mưa lũ ở Trung Quốc</p>\r\n<p>N&oacute;i về nguy&ecirc;n nh&acirc;n của đợt mưa lớn đang diễn ra ở H&agrave; Giang v&agrave; c&aacute;c tỉnh miền n&uacute;i Bắc Bộ, &ocirc;ng B&ugrave;i Đức Tuấn cho biết đ&acirc;y l&agrave; một trong những hiện tượng do &aacute;p cao cận nhiệt đới lấn s&acirc;u v&agrave;o nội địa. H&igrave;nh th&aacute;i n&agrave;y kết hợp với xo&aacute;y v&agrave; gi&oacute; tr&ecirc;n cao tồn tại l&acirc;u ở khu vực H&agrave; Giang.</p>\r\n<p>\"V&ugrave;ng xo&aacute;y n&agrave;y đ&atilde; xuất hiện 1-2 ng&agrave;y v&agrave; chỉ tồn tại ở th&agrave;nh phố H&agrave; Giang chứ kh&ocirc;ng di chuyển đi c&aacute;c nơi kh&aacute;c. Đ&oacute; l&agrave; l&yacute; do lượng mưa c&aacute;c nơi kh&ocirc;ng đồng đều\", &ocirc;ng Tuấn cho biết.</p>\r\n<p>Gi&aacute;m đốc Đ&agrave;i Kh&iacute; tượng Thủy văn khu vực Việt Bắc cũng cho biết c&aacute;c đợt mưa lớn th&ocirc;ng thường cũng chỉ ghi nhận lượng mưa tr&ecirc;n 200 mm. Tuy nhi&ecirc;n, với lượng mưa tr&ecirc;n 350 mm tại th&agrave;nh phố H&agrave; Giang trong v&ograve;ng 1 ng&agrave;y, cơ quan kh&iacute; tượng sẽ so s&aacute;nh lại c&aacute;c số liệu trong qu&aacute; khứ để nhận định xem đ&acirc;y c&oacute; phải trận mưa lớn lịch sử hay kh&ocirc;ng.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_4_2.jpg\" /></p>\r\n<p>Chuy&ecirc;n gia b&aacute;c bỏ th&ocirc;ng tin mưa lớn tại H&agrave; Giang do ảnh hưởng từ h&igrave;nh th&aacute;i g&acirc;y mưa của Trung Quốc. Ảnh: H.H.</p>\r\n<p>&Ocirc;ng Tuấn cũng b&aacute;c bỏ th&ocirc;ng tin mưa lớn ở H&agrave; Giang s&aacute;ng 21/7 do ảnh hưởng bởi h&igrave;nh th&aacute;i g&acirc;y mưa lũ tại Trung Quốc. Theo đ&oacute;, mưa xuất hiện tại chỗ, do ch&iacute;nh v&ugrave;ng m&acirc;y hội tụ h&igrave;nh th&agrave;nh tại khu vực, kh&ocirc;ng phải do m&acirc;y từ những nơi kh&aacute;c di chuyển đến.</p>\r\n<p>Trao đổi với Zing trước đ&oacute;, &ocirc;ng Vũ Đức Long, Ph&oacute; gi&aacute;m đốc Trung t&acirc;m dự b&aacute;o Kh&iacute; tượng Thủy văn Quốc gia, cũng khẳng định dải m&acirc;y hội tụ Front Mei-yu g&acirc;y mưa lũ lịch sử tại Trung Quốc kh&ocirc;ng thể ảnh hưởng đến Việt Nam.</p>\r\n<p>Chuy&ecirc;n gia giải th&iacute;ch dải m&acirc;y đang g&acirc;y mưa lũ lịch sử ở Trung Quốc v&agrave; Nhật Bản l&agrave; h&igrave;nh thế đặc trưng của khu vực n&agrave;y. Đ&acirc;y l&agrave; h&igrave;nh th&aacute;i thời tiết điển h&igrave;nh v&agrave; chỉ xuất hiện trọng phạm vi nhất định, kh&ocirc;ng di chuyển từ v&ugrave;ng n&agrave;y sang v&ugrave;ng kia.</p>\r\n<p>Th&ocirc;ng tin th&ecirc;m về t&igrave;nh h&igrave;nh mưa trong những ng&agrave;y tới tại c&aacute;c tỉnh miền n&uacute;i ph&iacute;a Bắc, cơ quan kh&iacute; tượng cho biết người d&acirc;n cần tiếp tục đề cao cảnh gi&aacute;c trong bối cảnh diễn biến thời tiết kh&oacute; lường v&agrave; cực đoan. Lượng mưa tại c&aacute;c nơi trong 2 ng&agrave;y tới phổ biến 40-80 mm/ng&agrave;y, c&oacute; nơi tr&ecirc;n 150 mm/ng&agrave;y.</p>\r\n<p>T&iacute;nh đến 15h ng&agrave;y 21/7, lượng mưa ghi nhận được tại nhiều nơi đ&atilde; vượt mức 300 mm, gồm: TP H&agrave; Giang 378 mm, Vị Xuy&ecirc;n 401 mm, Ho&agrave;ng Su Ph&igrave; 103 mm. Trận mưa k&eacute;o d&agrave;i trong nhiều giờ khiến TP H&agrave; Giang ngập trong biển nước.</p>\r\n<p>Thống k&ecirc; ban đầu cho thấy mưa lớn tại H&agrave; Giang khiến 5 người thiệt mạng do bị đất đ&aacute; v&ugrave;i lấp v&agrave; bị lũ cuốn, 2 người bị thương. Hơn 50 ng&ocirc;i nh&agrave; bị sạt lở, 524 nh&agrave; bị ngập lụt.</p>\r\n<p>Đ&aacute;ng ch&uacute; &yacute;, 2 nh&agrave; m&aacute;y thủy điện Thuận H&ograve;a (huyện Vị Xuy&ecirc;n) v&agrave; Th&aacute;i An (huyện Quản Bạ) đ&atilde; dừng hoạt động do bị đất đ&aacute; v&ugrave;i lấp.</p>',	'Địa hình của Hà Giang như lòng chảo. Khi mưa trút xuống với cường độ lớn, nước mưa không kịp thoát gây ngập úng nghiêm trọng.',	0,	2,	'2020-08-17 00:00:16',	NULL,	13,	3,	5),
(5,	'Cảnh sát tìm được nhiều người trong vụ chạy xe bốc đầu ở Bạc Liêu',	'<p>Hai ng&agrave;y qua, nhiều người d&acirc;n TP Bạc Li&ecirc;u (Bạc Li&ecirc;u) rất bức x&uacute;c khi xem clip tr&ecirc;n mạng x&atilde; hội c&oacute; nội dung nhiều thanh ni&ecirc;n chở th&ugrave;ng h&aacute;t di động chạy d&agrave;n h&agrave;ng ngang v&agrave; bốc đầu. Họ chạy tr&ecirc;n nhiều tuyến đường nội đ&ocirc; TP Bạc Li&ecirc;u, g&acirc;y mất trật tự an to&agrave;n giao th&ocirc;ng.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_5_1.jpg\" /></p>\n<p>Một thanh ni&ecirc;n trong nh&oacute;m chạy xe m&aacute;y bốc đầu. Ảnh chụp m&agrave;n h&igrave;nh.</p>\n<p>Ng&agrave;y 28/7, trung t&aacute; Phan Th&agrave;nh Được, Ph&oacute; c&ocirc;ng an TP Bạc Li&ecirc;u (Bạc Li&ecirc;u) cho biết lực lượng chức năng đ&atilde; x&aacute;c định được 4 thanh ni&ecirc;n li&ecirc;n quan đến vụ chạy xe g&acirc;y mất trật tự n&agrave;y.</p>\n<p>Bốn người bị cảnh s&aacute;t mời l&agrave;m việc gồm: Phan Minh Đăng (19 tuổi, ngụ huyện Vĩnh Lợi), L&ecirc; Thị Như Ph&uacute;c (21 tuổi, ngụ TP C&agrave; Mau), Nguyễn Huyền Thoại (21 tuổi, ngụ TP C&agrave; Mau) v&agrave; L&ecirc; Trưởng Nam (20 tuổi, ở huyện Vĩnh Lợi, Bạc Li&ecirc;u).</p>\n<p>Ngo&agrave;i ra, cảnh s&aacute;t c&ograve;n x&aacute;c định c&oacute; 4 xe m&aacute;y kh&aacute;c (2 chiếc biển số C&agrave; Mau, 2 biển số Bạc Li&ecirc;u) tham gia vụ d&agrave;n h&agrave;ng ngang chạy tr&ecirc;n đường. Phan Minh Đăng l&agrave; người quay clip đăng l&ecirc;n Facebook v&agrave;o ng&agrave;y 26/7.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_5_2.jpg\" /></p>\n<p>Một trong những thanh ni&ecirc;n trong nh&oacute;m bị cảnh s&aacute;t giao th&ocirc;ng mời l&agrave;m việc. Ảnh: T.T.</p>\n<p>&ldquo;C&ocirc;ng an th&agrave;nh phố tiếp tục chỉ đạo cảnh s&aacute;t giao th&ocirc;ng v&agrave; trật tự x&aacute;c minh, l&agrave;m r&otilde; nhựng người chạy c&aacute;c xe c&ograve;n lại để xử l&yacute; nghi&ecirc;m theo quy định của ph&aacute;p luật. Những người trong vụ n&agrave;y sẽ được kiểm tra xem c&oacute; dương t&iacute;nh với chất ma t&uacute;y hay kh&ocirc;ng để đưa v&agrave;o diện quản l&yacute;&rdquo;, l&atilde;nh đạo C&ocirc;ng an TP Bạc Li&ecirc;u n&oacute;i.</p>\n<p>Theo Điểm b, c Khoản 8 Điều 6 Nghị định 100/2019 của Ch&iacute;nh phủ quy định phạt tiền 6-8 triệu đồng đối với người điều khiển xe lạng l&aacute;ch hoặc đ&aacute;nh v&otilde;ng tr&ecirc;n đường bộ trong, ngo&agrave;i đ&ocirc; thị; điều khiển xe chạy bằng một b&aacute;nh đối với xe hai b&aacute;nh, chạy bằng hai b&aacute;nh đối với xe ba b&aacute;nh.</p>',	'Công an TP Bạc Liêu đã truy ra biển số của 8 chiếc xe tham gia dàn hàng ngang và bốc đầu để chạy một bánh.',	1,	2,	'2020-07-28 15:46:25',	NULL,	3,	3,	4),
(6,	'Nghi án giết vợ sắp cưới rồi tự vẫn',	'<p>Vụ &aacute;n mạng được ph&aacute;t hiện s&aacute;ng 9/6 tại khu nh&agrave; trọ ở ấp Phước H&ograve;a, x&atilde; H&ograve;a Ph&uacute;, huyện Long Hồ, tỉnh Vĩnh Long.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_6_1.jpg\" /></p>\n<p>Hiện trường nơi xảy ra vụ việc. Ảnh: Anh Minh.</p>\n<p>Cảnh s&aacute;t đến hiện trường, ph&aacute;t hiện chị Ng&ocirc; Thị Cẩm T&uacute; (27 tuổi) đ&atilde; tử vong b&ecirc;n trong ph&ograve;ng trọ. Anh Huỳnh Nguyễn Thiện Khi&ecirc;m (28 tuổi, c&ugrave;ng ngụ tỉnh Vĩnh Long) nằm trong ph&ograve;ng, tr&ecirc;n cổ tay c&oacute; vết thương.</p>\n<p>Theo một cảnh s&aacute;t, chị T&uacute; đ&atilde; tử vong, anh Khi&ecirc;m đang cấp cứu tại bệnh viện.</p>\n<p>C&aacute;c nh&acirc;n chứng n&oacute;i anh Khi&ecirc;m thu&ecirc; ph&ograve;ng trọ chung sống như vợ chồng với chị T&uacute;. H&agrave;ng ng&agrave;y, chị T&uacute; sang Cần Thơ l&agrave;m việc đến tối th&igrave; quay về ph&ograve;ng trọ. Anh Khi&ecirc;m l&agrave;m nghề x&acirc;y dựng, &iacute;t khi về ph&ograve;ng trọ.</p>\n<p>Một người th&acirc;n của chị T&uacute; cho biết hai người dự định kết h&ocirc;n nhưng kh&ocirc;ng hợp tuổi n&ecirc;n ho&atilde;n lại.</p>',	'Chị Tú được phát hiện tử vong bên trong phòng trọ, trên người anh Khiêm có vết thương nên được đưa đến bệnh viện cấp cứu.',	0,	2,	'2020-07-28 15:46:29',	NULL,	3,	3,	8),
(7,	'NHỮNG NGƯỜI KIẾM TIỀN THẦN TỐC VÌ CƠN SỐT VACCINE COVID-19',	'<p>Ng&agrave;y 26/7, Vaxart, một c&ocirc;ng ty nhỏ ở San Francisco bất ngờ th&ocirc;ng b&aacute;o loại vaccine Covid-19 m&agrave; doanh nghiệp n&agrave;y đang ph&aacute;t triển đ&atilde; được chọn v&agrave;o dự &aacute;n Operation Warp Speed của ch&iacute;nh phủ Mỹ.</p>\r\n<p>Operation Warp Speed l&agrave; nỗ lực của ch&iacute;nh phủ Mỹ trong việc phối hợp với c&aacute;c doanh nghiệp tư nh&acirc;n nhằm ph&aacute;t triển c&aacute;c giải ph&aacute;p y tế cho dịch Covid-19 trong thời gian ngắn nhất c&oacute; thể.</p>\r\n<p>Cổ phiếu của Vaxart tăng vọt sau th&ocirc;ng b&aacute;o n&oacute;i tr&ecirc;n. Quyền chọn mua cổ phiếu của ban l&atilde;nh đạo c&ocirc;ng ty n&agrave;y c&oacute; gi&aacute; trị cao gấp s&aacute;u lần so với v&agrave;i tuần trước đ&oacute;.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_7_1.jpg\" /></p>\r\n<p>Gi&aacute; cổ phiếu Vaxart tăng mạnh sau khi th&ocirc;ng b&aacute;o h&atilde;ng n&agrave;y được chọn v&agrave;o Operation Warp Speed. Ảnh: RTTNews.</p>\r\n<p>Quỹ đầu tư mạo hiểm Armistice Capital vốn sở hữu một phần Vaxart đ&atilde; b&aacute;n lượng lớn cổ phiếu v&agrave; thu về 200 triệu USD.</p>\r\n<p>Nhiều c&ocirc;ng ty v&agrave; nh&agrave; đầu tư dự đo&aacute;n rằng những doanh nghiệp chiến thắng trong cuộc chạy đua điều chế vaccine Covid-19 sẽ thu được lợi nhuận khổng lồ từ việc b&aacute;n h&agrave;ng trăm triệu, thậm ch&iacute; h&agrave;ng tỷ liều vaccine.</p>\r\n<p>Những người đứng đầu c&aacute;c doanh nghiệp thuộc ng&agrave;nh dược phẩm v&agrave; y tế đang tận dụng bối cảnh đ&oacute; v&agrave; kiếm được h&agrave;ng triệu USD sau khi c&ocirc;ng bố những chuyển biến t&iacute;ch cực trong việc ph&aacute;t triển vaccine, v&iacute; dụ như sự hỗ trợ từ ch&iacute;nh phủ, trong nỗ lực chống lại đại dịch.</p>\r\n<p>Bước ph&aacute;t triển trong điều chế vaccine hay chi&ecirc;u b&agrave;i quảng c&aacute;o?</p>\r\n<p>Theo Equilar, kể từ th&aacute;ng 3, ban l&atilde;nh đạo cũng như nh&acirc;n vi&ecirc;n của &iacute;t nhất 11 c&ocirc;ng ty thuộc ng&agrave;nh dược phẩm v&agrave; y tế đ&atilde; b&aacute;n hơn 1 tỷ USD cổ phiếu. Phần lớn trong số n&agrave;y l&agrave; c&aacute;c doanh nghiệp nhỏ, sự ph&aacute;t triển chủ yếu phụ thuộc v&agrave;o việc tập trung sản xuất một loại thuốc duy nhất.</p>\r\n<p>Tuy vậy, một số doanh nghiệp đang bị ch&iacute;nh phủ lưu &yacute; c&oacute; khả năng sử dụng t&ecirc;n của dự &aacute;n Operation Warp Speed như một chiến lược quảng c&aacute;o.</p>\r\n<p>Trong trường hợp của Vaxart, ti&ecirc;u đề trong th&ocirc;ng c&aacute;o của c&ocirc;ng ty n&agrave;y ghi: &ldquo;Vaccine Covid-19 của Vaxart được chọn v&agrave;o dự &aacute;n Operation Warp Speed của ch&iacute;nh phủ Mỹ&rdquo;. Tuy nhi&ecirc;n, thực tế kh&ocirc;ng đơn giản như vậy.</p>\r\n<p>Vaccine m&agrave; Vaxart ph&aacute;t triển được một cơ quan thuộc ch&iacute;nh phủ c&oacute; li&ecirc;n hệ với dự &aacute;n Operation Warp Speed chọn để thử tr&ecirc;n linh trưởng. Nhưng bản th&acirc;n c&ocirc;ng ty Vaxart kh&ocirc;ng nhận được hỗ trợ t&agrave;i ch&iacute;nh đ&aacute;ng kể từ Warp Speed để điều chế vaccine.</p>\r\n<p>Trợ l&yacute; Bộ trưởng Y tế Mỹ Michael R. Caputo cho biết: &ldquo;Bộ Y tế đ&atilde; k&yacute; những thỏa thuận hỗ trợ t&agrave;i ch&iacute;nh cho một số c&ocirc;ng ty điều chế vaccine, đồng thời đang đ&agrave;m ph&aacute;n với một số doanh nghiệp kh&aacute;c. Nhưng Vaxart kh&ocirc;ng nằm trong trường hợp n&agrave;o kể tr&ecirc;n&rdquo;.</p>\r\n<p>&ldquo;Vaccine của Vaxart đ&atilde; được chọn để tham gia v&agrave;o c&aacute;c nghi&ecirc;n cứu sơ bộ của ch&iacute;nh phủ nhằm x&aacute;c định những đối t&aacute;c v&agrave; nguồn lực tiềm năng cho Operation Warp Speed. V&agrave;o thời điểm hiện tại, c&aacute;c nghi&ecirc;n cứu đ&oacute; vẫn đang được tiến h&agrave;nh v&agrave; Bộ Y tế chưa đưa ra quyết định cụ thể n&agrave;o&rdquo;, &ocirc;ng Caputo n&oacute;i.</p>\r\n<p>Nhiều quan chức thuộc Bộ Y tế Mỹ lo ngại rằng Vaxart đang t&igrave;m c&aacute;ch tăng gi&aacute; cổ phiếu bằng việc ph&oacute;ng đại vai tr&ograve; của họ trong dự &aacute;n Operation Warp Speed.</p>\r\n<p>&ldquo;Vaxart tu&acirc;n thủ những chỉ thị v&agrave; quy định của ch&iacute;nh phủ, đồng thời đưa ra c&aacute;c quyết định nhằm tối ưu h&oacute;a lợi nhuận cho c&ocirc;ng ty v&agrave; c&aacute;c cổ đ&ocirc;ng&rdquo;, &ocirc;ng Andrei Floroiu, gi&aacute;m đốc điều h&agrave;nh của Vaxart, ph&aacute;t biểu h&ocirc;m 24/7.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_7_2.jpg\" /></p>\r\n<p>&Ocirc;ng Andrei Floroius cho biết Vaxart lu&ocirc;n tu&acirc;n thủ những quy định m&agrave; ch&iacute;nh phủ đặt ra. Ảnh: Will Ragozzino.</p>\r\n<p>Về dự &aacute;n Operation Warp Speed (O.W.S), &ocirc;ng Floroiu n&oacute;i th&ecirc;m: &ldquo;Ch&uacute;ng t&ocirc;i tin rằng vaccine m&agrave; Vaxart ph&aacute;t triển l&agrave; loại vaccine th&uacute; vị nhất trong khu&ocirc;n khổ O.W.S, bởi ch&uacute;ng l&agrave; loại vaccine Covid-19 duy nhất thuộc dạng uống được chọn tham gia O.W.S&rdquo;.</p>\r\n<p>Những người hưởng lợi từ cuộc khủng hoảng của to&agrave;n x&atilde; hội</p>\r\n<p>Giao dịch chứng kho&aacute;n đ&uacute;ng thời điểm thường kh&ocirc;ng được coi l&agrave; phạm ph&aacute;p ở Mỹ. Nhưng c&aacute;c nh&agrave; đầu tư v&agrave; chuy&ecirc;n gia cho rằng phương thức giao dịch n&agrave;y sẽ khiến mọi người c&oacute; cảm gi&aacute;c rằng l&atilde;nh đạo c&aacute;c doanh nghiệp đang thu lợi từ nguồn tin nội bộ, từ đ&oacute; l&agrave;m x&oacute;i m&ograve;n niềm tin của c&ocirc;ng ch&uacute;ng v&agrave;o ng&agrave;nh dược phẩm trong bối cảnh thế giới đang tr&ocirc;ng đợi những c&ocirc;ng ty n&agrave;y t&igrave;m ra giải ph&aacute;p điều trị Covid-19.</p>\r\n<p>&ldquo;L&atilde;nh đạo c&aacute;c doanh nghiệp dược phẩm đang kiếm tiền từ cuộc khủng hoảng của to&agrave;n x&atilde; hội, điều n&agrave;y kh&ocirc;ng ph&ugrave; hợp ch&uacute;t n&agrave;o&rdquo;, Ben Wakana, người đứng đầu tổ chức phi lợi nhuận Patients for Affordable Drugs, n&oacute;i. &ldquo;Mỗi ng&agrave;y, to&agrave;n nước Mỹ phải đ&aacute;nh đổi nhiều thứ để chống chọi với đại dịch, c&ograve;n c&aacute;c c&ocirc;ng ty dược phẩm xem đ&acirc;y như một cơ hội để kiếm tiền&rdquo;.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_7_3.jpg\" width=\"500\" height=\"333\" /></p>\r\n<p>Ben Wakana. Ảnh: Twitter.</p>\r\n<p>Cổ phiếu của c&ocirc;ng ty c&ocirc;ng nghệ sinh học Regeneron ở New York đ&atilde; tăng gần 80% kể từ đầu th&aacute;ng 2 sau khi th&ocirc;ng b&aacute;o họ đ&atilde; hợp t&aacute;c với Bộ y tế để ph&aacute;t triển phương ph&aacute;p điều trị Covid-19.</p>\r\n<p>C&aacute;c th&agrave;nh vi&ecirc;n hội đồng quản trị của Regeneron đ&atilde; b&aacute;n tổng cộng gần 700 triệu USD kể từ th&aacute;ng 2. Hồi th&aacute;ng 5, Gi&aacute;m đốc điều h&agrave;nh Leonard Schleifer thậm ch&iacute; đ&atilde; b&aacute;n tới 178 triệu USD cổ phiếu trong vỏn vẹn một ng&agrave;y.</p>\r\n<p>Tuy nhi&ecirc;n, b&agrave; Alexandra Bowie, người ph&aacute;t ng&ocirc;n của Regeneron, cho biết hầu hết giao dịch đ&oacute; đ&atilde; được l&ecirc;n lịch sẵn th&ocirc;ng qua một chương tr&igrave;nh tự động b&aacute;n cổ phiếu của c&aacute;c th&agrave;nh vi&ecirc;n quản trị khi gi&aacute; cổ phiếu đạt ngưỡng nhất định.</p>\r\n<p>C&ocirc;ng ty Moderna ở Massachusetts, chưa từng cho ra mắt bất kỳ loại thuốc n&agrave;o trong 10 năm hoạt động, bất ngờ được định gi&aacute; 30 triệu USD sau khi đăng tải th&ocirc;ng tin về những chuyển biến t&iacute;ch cực m&agrave; c&ocirc;ng ty n&agrave;y đạt được trong việc ph&aacute;t triển vaccine Covid-19.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_7_4.jpg\" /></p>\r\n<p>Modern c&oacute; trụ sở tại Cambridge, Massachusetts. Ảnh: Bloomberg.</p>\r\n<p>Ban l&atilde;nh đạo của Moderna đ&atilde; b&aacute;n khoảng 248 triệu USD cổ phiếu kể từ sau khi đưa ra th&ocirc;ng b&aacute;o c&ocirc;ng ty n&agrave;y đang nghi&ecirc;n cứu điều chế vaccine đặc trị virus corona hồi cuối th&aacute;ng 1.</p>\r\n<p>Theo b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh nộp Ủy ban chứng kho&aacute;n v&agrave; giao dịch Mỹ, chỉ một v&agrave;i giao dịch được l&ecirc;n lịch từ trước v&agrave; phần lớn trong số c&ograve;n lại hầu hết l&agrave; những quyết định tức thời.</p>\r\n<p>Novavax, một c&ocirc;ng ty dược phẩm ở Gaithersburg, bang Maryland bắt đầu nghi&ecirc;n cứu một loại vaccine v&agrave;o đầu năm nay. Trong v&ograve;ng v&agrave;i th&aacute;ng, c&ocirc;ng ty n&agrave;y đ&atilde; b&aacute;o c&aacute;o kết quả thử nghiệm sơ bộ đầy hứa hẹn v&agrave; một thỏa thuận trị gi&aacute; 1,6 tỷ USD với ch&iacute;nh quyền Trump.</p>\r\n<p>Từ đ&oacute; đến nay, gi&aacute; cổ phiếu của Novavax tăng với tốc độ ch&oacute;ng mặt, từ 24 USD l&ecirc;n 130 USD mỗi cổ phiếu. Tr&ecirc;n giấy tờ, quyền mua cổ phiếu của bốn gi&aacute;m đốc điều h&agrave;nh Novavax c&oacute; gi&aacute; trị hơn 100 triệu USD.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_7_5.jpg\" width=\"500\" height=\"281\" /></p>\r\n<p>C&aacute;c doanh nghiệp ng&agrave;nh dược đang rất r&aacute;o riết trong cuộc chạy đua điều chế vaccine Covid-19. Ảnh: Euro News.</p>\r\n<p>Tuy nhi&ecirc;n, Vaxart mới l&agrave; c&ocirc;ng ty kiếm tiền nhanh nhất. Cổ phiếu h&atilde;ng n&agrave;y hồi đầu năm vẫn c&ograve;n ở mức 0,35 USD, nay đ&atilde; tăng l&ecirc;n đến 12,3 USD, chủ yếu l&agrave; sau khi Vaxart tuy&ecirc;n bố nghi&ecirc;n cứu vaccine Covid-19 dạng uống.</p>\r\n<p>Tr&ecirc;n thực tế, Vaxart vẫn chưa đưa ra thị trường bất kỳ loại vaccine n&agrave;o, đồng thời h&atilde;ng n&agrave;y cũng chỉ c&oacute; 15 nh&acirc;n vi&ecirc;n. Tuy nhi&ecirc;n, c&ocirc;ng ty n&agrave;y lu&ocirc;n biết c&aacute;ch đưa ra những th&ocirc;ng c&aacute;o c&oacute; thể đẩy gi&aacute; cổ phiếu của họ l&ecirc;n nhanh trong thời gian ngắn.</p>\r\n<p>V&iacute; dụ, ng&agrave;y 25/7, Vaxart th&ocirc;ng b&aacute;o rằng doanh nghiệp n&agrave;y đ&atilde; k&yacute; một l&aacute; thư với dự định hợp t&aacute;c c&ugrave;ng một c&ocirc;ng ty kh&aacute;c để sản xuất vaccine Covid-19 số lượng lớn. Gi&aacute; cổ phiếu của họ tăng gấp đ&ocirc;i trong vỏn vẹn một ng&agrave;y.</p>\r\n<p>Ng&agrave;y 26/7, Vaxart đưa ra th&ocirc;ng c&aacute;o rằng h&atilde;ng n&agrave;y đ&atilde; được chọn tham gia dự &aacute;n Operation Warp Speed. Ngay lập tức, cổ phiếu của họ lại tăng gấp đ&ocirc;i, c&aacute;n mốc 14 USD, mức cao nhất trong nhiều năm trở lại đ&acirc;y.</p>\r\n<p>&ldquo;Vaxart đang tạo ra những đột ph&aacute; trong ng&agrave;nh c&ocirc;ng nghiệp điều chế vaccine&rdquo;, gi&aacute;m đốc điều h&agrave;nh Floroiu của h&atilde;ng n&agrave;y cho biết trong một buổi họp trực tuyến gần đ&acirc;y. &ldquo;Việc kiếm tiền từ vaccine đ&acirc;u c&oacute; g&igrave; đ&aacute;ng l&ecirc;n &aacute;n, miễn đừng trục lợi l&agrave; được&rdquo;.</p>\r\n<p>Cả thế giới chạy đua t&igrave;m phương thuốc ngăn chặn virus corona</p>\r\n<p>C&aacute;c ph&ograve;ng th&iacute; nghiệm tr&ecirc;n to&agrave;n thế giới đang dốc sức để t&igrave;m ra loại vaccine mới. Hiện nay, tất cả đều mới chỉ l&agrave; thử nghiệm l&acirc;m s&agrave;ng nhưng đ&atilde; thu về nhiều hiệu ứng t&iacute;ch cực.</p>',	'Đầu tư đặt cược vào cổ phiếu đúng thời điểm đem về lợi nhuận khổng lồ cho giới lãnh đạo của các doanh nghiệp nghiên cứu phát triển phác đồ điều trị và vaccine đặc trị virus corona.',	1,	2,	'2020-08-17 00:00:16',	NULL,	5,	3,	9),
(8,	'Sau căng thẳng với TQ, Mỹ - Ấn tập trận tại eo biển Malacca',	'<p>Chỉ v&agrave;i ng&agrave;y sau khi kết th&uacute;c cuộc tập trận hải qu&acirc;n quy m&ocirc; lớn nhất kể từ năm 2014, nh&oacute;m t&aacute;c chiến t&agrave;u s&acirc;n bay Mỹ đ&atilde; tiến v&agrave;o eo biển Malacca để tập trận c&ugrave;ng Hải qu&acirc;n Ấn Độ, Hải qu&acirc;n Ấn Độ cho biết h&ocirc;m 20/7.</p>\n<p>Hải qu&acirc;n Mỹ cho biết c&aacute;c t&agrave;u chiến của hải qu&acirc;n hai nước - gồm t&agrave;u s&acirc;n bay USS Nimitz, tuần dương hạm USS Princeton, t&agrave;u khu trục mang t&ecirc;n lửa dẫn đường USS Sterett v&agrave; USS Ralph Johnson của Hải qu&acirc;n Mỹ - v&agrave; c&aacute;c t&agrave;u chiến Rana, Sahyadri, Shivalik v&agrave; Kamorta của Hải qu&acirc;n Ấn Độ đ&atilde; tham gia cuộc tập trận nhằm tối ưu h&oacute;a khả năng huấn luyện v&agrave; tương t&aacute;c, bao gồm ph&ograve;ng kh&ocirc;ng.</p>\n<p>&ldquo;Khả năng tương t&aacute;c v&agrave; ph&ograve;ng kh&ocirc;ng để chống lại ai? R&otilde; r&agrave;ng kh&ocirc;ng phải l&agrave; cướp biển hay khủng bố. Đ&acirc;y l&agrave; loại h&igrave;nh hợp t&aacute;c để đối ph&oacute; với đối thủ m&agrave; kh&ocirc;ng ai kh&aacute;c ch&iacute;nh l&agrave; Trung Quốc&rdquo;, Nikkei Asia Review dẫn lời Derek Grossman, nh&agrave; ph&acirc;n t&iacute;ch tại Tập đo&agrave;n RAND, n&oacute;i.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_8_1.jpg\" /></p>\n<p>T&agrave;u s&acirc;n bay Nimitz đang đi qua Singapore để tiến v&agrave;o eo biển Malacca. Ảnh: Hải qu&acirc;n Mỹ.</p>\n<p>Trong nhiều thập kỷ, Ấn Độ theo đuổi ch&iacute;nh s&aacute;ch đối ngoại được x&acirc;y dựng dựa tr&ecirc;n sự kh&ocirc;ng li&ecirc;n kết. H&ocirc;m 20/7, Ngoại trưởng Ấn Độ Subrahmanyam Jaishankar nhắc lại quan điểm tr&ecirc;n. Tuy nhi&ecirc;n, cả Ấn Độ v&agrave; Mỹ đều đang ở giữa căng thẳng với Trung Quốc.</p>\n<p>&ldquo;Cuộc cạnh tranh giữa Trung Quốc v&agrave; Mỹ c&oacute; thể đang t&aacute;c động đến Ấn Độ. New Delhi đ&atilde; trung lập trong nhiều năm, nhưng xem x&eacute;t mối quan hệ gần đ&acirc;y với Bắc Kinh, Ấn Độ nhận ra rằng việc duy tr&igrave; mối quan hệ th&acirc;n mật với Trung Quốc sẽ rất kh&oacute; khăn&rdquo;, nh&agrave; ph&acirc;n t&iacute;ch Grossman n&oacute;i.</p>\n<p>Trong th&aacute;ng 6, binh sĩ hai nước đ&atilde; xảy ra đụng độ ở khu vực bi&ecirc;n giới đang tranh chấp tr&ecirc;n d&atilde;y Himalaya. 20 binh sĩ Ấn Độ đ&atilde; thiệt mạng, ph&iacute;a Trung Quốc kh&ocirc;ng b&aacute;o c&aacute;o con số thương vong.</p>\n<p>Điều n&agrave;y xảy ra khi Trung Quốc mở rộng sự hiện diện về ph&iacute;a Nam &Aacute;. Ngo&agrave;i việc hỗ trợ cho đối thủ l&acirc;u đời của Ấn Độ l&agrave; Pakistan, Trung Quốc đang t&igrave;m c&aacute;ch g&acirc;y ảnh hưởng ở Bangladesh, Nepal v&agrave; Sri Lanka.</p>\n<p>&ldquo;New Delhi nhận ra rằng, Bắc Kinh đ&atilde; g&acirc;y ảnh hưởng ở khắp nơi, kh&ocirc;ng chỉ ở v&ugrave;ng n&uacute;i, Ấn Độ Dương m&agrave; c&ograve;n t&igrave;m c&aacute;ch k&eacute;o c&aacute;c quốc gia nhỏ hơn ra khỏi quỹ đạo của Ấn Độ. Giờ đ&acirc;y, cuộc tập trận với Hải qu&acirc;n Mỹ l&agrave; một phần trong những chiến lược mới của Ấn Độ&rdquo;, nh&agrave; ph&acirc;n t&iacute;ch Grossman n&oacute;i.</p>\n<p>&Ocirc;ng Grossman cho biết th&ecirc;m việc thiết lập sự hiện diện qu&acirc;n sự ở eo biển Malacca c&oacute; vai tr&ograve; rất quan trọng trong việc đối ph&oacute; Trung Quốc. Bắc Kinh đang ở v&agrave;o thế tiến tho&aacute;i lưỡng nan. Họ sẽ l&agrave;m g&igrave; nếu đối thủ chặn eo biển n&agrave;y trong một cuộc xung đột. Malacca l&agrave; điểm trung chuyển rất quan trọng đối với dầu mỏ v&agrave; t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n kh&aacute;c cho Trung Quốc.</p>',	'Nhóm tác chiến tàu sân bay Mỹ cùng Hải quân Ấn Độ tập trận trên eo biển Malacca, tuyến đường thủy quan trọng với Trung Quốc khi căng thẳng trong khu vực leo thang.',	0,	2,	'2020-07-28 16:06:41',	NULL,	0,	3,	9),
(9,	'Triều Tiên báo động khẩn cấp ở Kaesong, chuyện gì đang xảy ra?',	'<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_9_1.jpg\" width=\"639\" height=\"426\" /></p>\n<p>H&ocirc;m 25/7, Bộ Ch&iacute;nh trị Trung ương đảng Lao Động Triều Ti&ecirc;n đ&atilde; họp khẩn cấp với sự chủ tr&igrave; của nh&agrave; l&atilde;nh đạo Kim Jong Un để b&agrave;n về việc nước n&agrave;y ph&aacute;t hiện một người nghi nhiễm Covid-19 ở th&agrave;nh phố Kaesong nằm ngay s&aacute;t bi&ecirc;n giới với H&agrave;n Quốc. &Ocirc;ng Kim cũng tuy&ecirc;n bố t&igrave;nh trạng khẩn cấp ở Kaesong v&agrave; phong toả th&agrave;nh phố n&agrave;y.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_9_2.jpg\" width=\"644\" height=\"429\" /></p>\n<p>Nh&agrave; l&atilde;nh đạo Triều Ti&ecirc;n y&ecirc;u cầu uỷ ban trung ương đảng chuyển sang trạng th&aacute;i khẩn cấp chống dịch v&agrave; cho biết đ&acirc;y l&agrave; \"t&igrave;nh huống nguy cấp c&oacute; thể cho l&agrave; virus nguy hiểm đ&atilde; x&acirc;m nhập v&agrave;o đất nước\". Người nghi nhiễm được truyền th&ocirc;ng Triều Ti&ecirc;n cho l&agrave; một c&ocirc;ng d&acirc;n đ&atilde; đ&agrave;o tẩu sang H&agrave;n Quốc c&aacute;ch đ&acirc;y 3 năm v&agrave; vừa trốn về nước v&agrave;o tuần trước.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_9_3.jpg\" width=\"643\" height=\"428\" /></p>\n<p>Nếu được x&aacute;c nhận, đ&acirc;y sẽ l&agrave; ca nhiễm Covid-19 đầu ti&ecirc;n được tuy&ecirc;n bố ch&iacute;nh thức của Triều Ti&ecirc;n. Giới l&atilde;nh đạo nước n&agrave;y coi c&aacute;c nỗ lực chống lại virus l&agrave; \"vấn đề sinh tồn của quốc gia\" v&agrave; hồi đầu năm nay đ&atilde; đ&oacute;ng cửa ho&agrave;n to&agrave;n bi&ecirc;n giới cũng như cấm kh&aacute;ch du lịch nước ngo&agrave;i nhập cảnh.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_9_4.jpg\" width=\"652\" height=\"434\" /></p>\n<p>Tại th&agrave;nh phố Paju của H&agrave;n Quốc s&aacute;t bi&ecirc;n giới Triều Ti&ecirc;n, kh&aacute;ch du lịch được y&ecirc;u cầu đeo khẩu trang để ngăn chặn sự l&acirc;y lan của virus corona. Qu&acirc;n đội H&agrave;n Quốc cho biết họ cũng đ&atilde; x&aacute;c định được c&aacute; nh&acirc;n (chưa c&ocirc;ng bố danh t&iacute;nh) vượt bi&ecirc;n tr&aacute;i ph&eacute;p sang Triều Ti&ecirc;n tuần trước.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_9_5.jpg\" width=\"658\" height=\"438\" /></p>\n<p>&Ocirc;ng Leif-Eric Easley, gi&aacute;o sư ng&agrave;nh quan hệ quốc tế tại Đại học Ewha ở Seoul, cho rằng việc Triều Ti&ecirc;n c&ocirc;ng bố ca nghi nhiễm Covid-19 đầu ti&ecirc;n đến từ H&agrave;n Quốc l&agrave; động th&aacute;i chiến lược, v&agrave; c&oacute; thể đ&acirc;y cũng l&agrave; chiến thuật nhằm g&acirc;y sức &eacute;p ngoại giao với H&agrave;n Quốc, cũng như để ngăn cản người Triều Ti&ecirc;n đ&agrave;o tẩu sang H&agrave;n Quốc trong tương lai.</p>\n<p>C&aacute;c cựu chiến binh Triều Ti&ecirc;n đeo khẩu trang tham gia lễ kỷ ni&ecirc;m 57 năm chiến tranh Triều Ti&ecirc;n ở B&igrave;nh Nhưỡng h&ocirc;m 25/7. Nước n&agrave;y hiện vẫn chưa ghi nhận ca nhiễm Covid-19 n&agrave;o.</p>',	'Việc Bình Nhưỡng quyết định phong toả thành phố công nghiệp Kaesong sau khi phát hiện một người nghi nhiễm Covid-19 được cho là một động thái gây bất ngờ.',	1,	2,	'2020-07-28 16:14:08',	NULL,	0,	3,	10),
(10,	'MỸ GIÁNG ĐÒN TRỪNG PHẠT ĐỒNG LOẠT LÊN TRUNG QUỐC',	'<p style=\"text-align: center;\"><iframe src=\"https://www.youtube.com/embed/HSfxRUJTTBA\" width=\"811\" height=\"456\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<p>Chiến lược mở rộng c&ocirc;ng k&iacute;ch v&agrave; trừng phạt Trung Quốc tr&ecirc;n nhiều phương diện leo thang v&agrave;o tuần qua. Ch&iacute;nh phủ Tổng thống Trump y&ecirc;u cầu Trung Quốc đ&oacute;ng cửa tổng l&atilde;nh sự qu&aacute;n ở Houston trong v&ograve;ng 72 tiếng, với c&aacute;o buộc thu thập th&ocirc;ng tin t&igrave;nh b&aacute;o. Mỹ xem ph&aacute;i bộ ngoại giao n&agrave;y l&agrave; \"t&acirc;m chấn\" của hoạt động đ&aacute;nh cắp nghi&ecirc;n cứu v&agrave; c&oacute; li&ecirc;n quan đến gian lận thị thực.</p>\n<p>Đ&acirc;y l&agrave; động th&aacute;i mới nhất trong một loạt đ&ograve;n trừng phạt của Washington nhắm v&agrave;o Bắc Kinh, từ &aacute;p lệnh trừng phạt kinh tế đối với giới chức Trung Quốc c&oacute; li&ecirc;n quan đến vấn đề người Uighur, bỏ tư c&aacute;ch quan hệ kinh tế đặc biệt với Hong Kong sau luật an ninh quốc gia g&acirc;y tranh c&atilde;i, đến l&ecirc;n kế hoạch trục xuất một số nh&agrave; b&aacute;o Trung Quốc v&agrave; giới hạn du học sinh tại Mỹ.</p>\n<p>C&ocirc;ng k&iacute;ch, trừng phạt li&ecirc;n tiếp</p>\n<p>\"M&ocirc; h&igrave;nh cũ của tiếp cận m&ugrave; qu&aacute;ng với Trung Quốc sẽ kh&ocirc;ng giải quyết được g&igrave;. Ch&uacute;ng ta kh&ocirc;ng được ph&eacute;p quay lại (với m&ocirc; h&igrave;nh đ&oacute;)\", Ngoại trưởng Mike Pompeo ph&aacute;t biểu ng&agrave;y 23/7. &Ocirc;ng c&ocirc;ng k&iacute;ch ch&iacute;nh s&aacute;ch của l&atilde;nh đạo Bắc Kinh tại Trung Quốc v&agrave; \"sự th&ugrave; địch với tự do ở mọi nơi kh&aacute;c cũng hung hăng hơn\". Nh&agrave; ngoại giao h&agrave;ng đầu của Mỹ cảnh b&aacute;o nếu \"thế giới tự do\" kh&ocirc;ng thay đổi giới l&atilde;nh đạo Trung Quốc th&igrave; điều ngược lại sẽ xảy ra.</p>\n<p>B&agrave;i ph&aacute;t biểu của &ocirc;ng Pompeo được đọc ở Thư viện Bảo t&agrave;ng Tổng thống Nixon ở California. Gần nửa thế kỷ trước, ch&iacute;nh Tổng thống Richard Nixon l&agrave; người mở quan hệ ngoại giao với Bắc Kinh. B&agrave;i ph&aacute;t biểu ng&agrave;y 23/7 c&oacute; thể nhằm đ&aacute;nh dấu bước thay đổi r&otilde; rệt trong ch&iacute;nh s&aacute;ch Mỹ đối với Trung Quốc h&agrave;ng thập kỷ qua, vốn bị Tổng thống Trump xem l&agrave; qu&aacute; mềm yếu, theo Washington Post.</p>\n<p>Trước &ocirc;ng Pompeo, một loạt quan chức cấp cao kh&aacute;c trong ch&iacute;nh phủ Mỹ cũng c&ocirc;ng k&iacute;ch gay gắt Bắc Kinh, bao gồm Cố vấn An ninh Quốc gia Robert O\'Brien, Bộ trưởng Tư ph&aacute;p William Barr v&agrave; Gi&aacute;m đốc Cục Điều tra Li&ecirc;n bang (FBI) Christopher Wray. C&aacute;c th&ocirc;ng điệp nhằm nhấn mạnh quyết t&acirc;m của Washington trong đối đầu với Bắc Kinh tr&ecirc;n nhiều vấn đề.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_10_1.jpg\" width=\"647\" height=\"422\" /></p>\n<p>Tổng thống Donald Trump tại một cuộc họp b&aacute;o về t&igrave;nh h&igrave;nh dịch Covid-19. Ảnh: Reuters.</p>\n<p>Tại Michigan, khoảng 2 tuần trước, Bộ trưởng Tư ph&aacute;p William Barr chĩa mũi c&ocirc;ng k&iacute;ch v&agrave;o m&ocirc; h&igrave;nh gi&aacute;m s&aacute;t v&agrave; kiểm duyệt của Bắc Kinh. &Ocirc;ng c&ograve;n điểm mặt một số c&ocirc;ng ty Mỹ \"nhượng bộ\" trước sức &eacute;p như Google, Apple, Microsoft v&agrave; Yahoo để trở th&agrave;nh \"qu&acirc;n tốt dưới ảnh hưởng Trung Quốc\".</p>\n<p>C&ugrave;ng giai đoạn n&agrave;y, Ngoại trưởng Pompeo t&iacute;ch cực vận động những nước đồng minh theo ch&acirc;n Mỹ cấm cửa Huawei v&agrave; c&ocirc;ng nghệ 5G của đối thủ. &Ocirc;ng c&ograve;n đề cập khả năng chặn lu&ocirc;n TikTok v&agrave; ứng dụng mạng x&atilde; hội Trung Quốc. Giữa th&aacute;ng 7, Bộ Ngoại giao Mỹ ch&iacute;nh thức xem c&aacute;c tuy&ecirc;n bố h&agrave;ng hải của Trung Quốc tr&ecirc;n Biển Đ&ocirc;ng l&agrave; \"tr&aacute;i ph&aacute;p luật\".</p>\n<p>Nội bộ chưa thống nhất</p>\n<p>Điều khiến nhiều nh&acirc;n vật chỉ tr&iacute;ch Tổng thống Trump lo ngại l&agrave; sự thay đổi ch&iacute;nh s&aacute;ch xảy đến c&aacute;ch cuộc bầu cử năm 2020 chưa đầy 4 th&aacute;ng. C&acirc;u hỏi được đặt ra l&agrave;: giữa bối cảnh Tổng thống Trump muốn Trung Quốc chịu tr&aacute;ch nhiệm về đại dịch v&agrave; hạ thấp đối thủ tranh cử Joe Biden yếu đuối trước Trung Quốc, liệu ch&iacute;nh phủ của &ocirc;ng đ&atilde; bị cuốn theo v&agrave; c&ocirc;ng k&iacute;ch m&agrave; kh&ocirc;ng nghĩ đến hệ quả? Một quan chức tại Washington tiết lộ c&aacute;ch tiếp cận n&agrave;y được mệnh danh l&agrave; \"giai đoạn đốt sạch hết\" trong ch&iacute;nh s&aacute;ch của họ với Trung Quốc.</p>\n<p>\"T&ocirc;i lo ngại s&acirc;u sắc trước c&aacute;ch tiếp cận của ch&iacute;nh phủ khi tin tưởng một c&aacute;ch sai lầm rằng ra vẻ đối đầu đồng nghĩa với mang t&iacute;nh cạnh tranh\", Thượng nghị sĩ Robert Menendez (D&acirc;n chủ) chia sẻ trong phi&ecirc;n chất vấn Ph&oacute; ngoại trưởng Stephen Biegun tuần qua về ch&iacute;nh s&aacute;ch đối ph&oacute; Trung Quốc. &Ocirc;ng Menendez nhất tr&iacute; rằng ch&iacute;nh s&aacute;ch của Mỹ cần cứng rắn hơn. Tuy nhi&ecirc;n, &ocirc;ng nhận định \"cứng rắn l&agrave; biện ph&aacute;p, kh&ocirc;ng phải mục ti&ecirc;u cuối c&ugrave;ng\".</p>\n<p>Một số nguồn tin tiết lộ chiến lược của Washington đối với Trung Quốc mới l&agrave; một dự &aacute;n dang dở. Một đồng minh Tổng thống Trump cho biết những ph&aacute;t biểu cứng rắn thời gian qua xuất ph&aacute;t từ mối lo ngại trong nh&oacute;m cố vấn ch&iacute;nh trị của &ocirc;ng. D&ugrave; nh&agrave; l&atilde;nh đạo c&ocirc;ng k&iacute;ch đối thủ tranh cử thiếu nhất qu&aacute;n về vấn đề Trung Quốc, họ lo sợ bản th&acirc;n Tổng thống Trump cũng đang lung lay trong vấn đề n&agrave;y.</p>\n<p>Nguồn tin n&agrave;y tiết lộ Bộ trưởng T&agrave;i ch&iacute;nh Mỹ Steve Mnuchin, vốn c&oacute; quan điểm chừng mực hơn về Trung Quốc, đ&atilde; từ chối ph&aacute;t biểu c&ocirc;ng k&iacute;ch Bắc Kinh. D&ugrave; chỉ tr&iacute;ch Trung Quốc thất bại trong việc ngăn chặn Covid-19 lan rộng, sự gay gắt của Tổng thống Trump cũng kh&ocirc;ng đi qu&aacute; xa như những c&aacute;o buộc m&agrave; nhiều trợ l&yacute; của &ocirc;ng từng n&ecirc;u.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_10_2.jpg\" width=\"592\" height=\"333\" /></p>\n<p>Đặc vụ v&agrave; quan chức an ninh Mỹ v&agrave;o Tổng l&atilde;nh sự qu&aacute;n Trung Quốc ở Houston h&ocirc;m 24/7. Ảnh: Reuters.</p>\n<p>Th&aacute;i độ của &ocirc;ng Trump đối với Trung Quốc thiếu sự nhất qu&aacute;n. &Ocirc;ng đắc cử c&ugrave;ng lời hứa ứng ph&oacute; Trung Quốc cứng rắn hơn. Song c&aacute;ch &ocirc;ng theo đuổi thỏa thuận thương mại song phương với Trung Quốc, ngại c&ocirc;ng k&iacute;ch trực tiếp nh&agrave; l&atilde;nh đạo Tập Cận B&igrave;nh hay c&ocirc;ng khai đề cập những ch&iacute;nh s&aacute;ch của Bắc Kinh... đ&atilde; cản trở việc h&igrave;nh th&agrave;nh một chiến lược to&agrave;n diện cho Washington.</p>\n<p>Hơn 2 tuần trước, Tổng thống Trump c&oacute; buổi họp b&aacute;o tuy&ecirc;n bố sẽ k&yacute; sắc lệnh trừng phạt Bắc Kinh li&ecirc;n quan đến luật an ninh quốc gia cho Hong Kong. Tuy nhi&ecirc;n, &ocirc;ng d&agrave;nh phần lớn thời gian để c&ocirc;ng k&iacute;ch đối thủ tranh cử v&agrave; ca ngợi th&agrave;nh tựu trong nhiệm kỳ. Đến buổi họp b&aacute;o tuần qua, nh&agrave; l&atilde;nh đạo để mở khả năng đ&oacute;ng cửa th&ecirc;m ph&aacute;i bộ ngoại giao Trung Quốc tại Mỹ. Thế nhưng, &ocirc;ng lại b&agrave;y tỏ &yacute; định hợp t&aacute;c với Bắc Kinh nếu c&aacute;c nh&agrave; khoa học Trung Quốc ph&aacute;t triển được vaccine Covid-19.</p>\n<p>Th&aacute;ng 6, cựu cố vấn an ninh quốc gia John Bolton c&ograve;n tiết lộ Tổng thống Trump từng cố thuyết phục &ocirc;ng Tập gi&uacute;p m&igrave;nh t&aacute;i đắc cử bằng c&aacute;ch để Trung Quốc mua th&ecirc;m h&agrave;ng h&oacute;a Mỹ. Đ&agrave;m ph&aacute;n thương mại \"đ&oacute;ng băng\" sau khi đại dịch b&ugrave;ng ph&aacute;t. V&agrave; kể từ sau cuộc điện đ&agrave;m th&aacute;ng 3, khi Mỹ kịch liệt l&ecirc;n &aacute;n Trung Quốc che giấu dịch bệnh, hai nh&agrave; l&atilde;nh đạo kh&ocirc;ng c&ograve;n n&oacute;i chuyện với nhau.</p>\n<p>Một số ch&iacute;nh trị gia cũng cho rằng Washington kh&ocirc;ng đưa ra được một m&ocirc; h&igrave;nh l&atilde;nh đạo thay thế của Mỹ. Tại phi&ecirc;n chất vấn của Ủy ban Đối ngoại Thượng viện, Thượng nghị sĩ Mitt Romney (Cộng h&ograve;a) đ&aacute;nh gi&aacute; chủ trương \"Nước Mỹ trước ti&ecirc;n\" của Tổng thống Trump đ&atilde; khiến mọi quốc gia kh&aacute;c \"tự theo đuổi lợi &iacute;ch của ri&ecirc;ng m&igrave;nh\" thay v&igrave; phối hợp đối ph&oacute; sự hung hăng từ Trung Quốc.</p>\n<p>Đ&aacute;p lại những ho&agrave;i nghi, Ph&oacute; ngoại trưởng Stephen Biegun lưu &yacute; những nh&agrave; hoạch định ch&iacute;nh s&aacute;ch Mỹ \"đ&atilde; đầu tư kh&ocirc;ng &iacute;t trong gần 3 thập kỷ qua\" t&igrave;m c&aacute;ch dỗ d&agrave;nh Trung Quốc trở th&agrave;nh một th&agrave;nh vi&ecirc;n quốc tế c&oacute; tr&aacute;ch nhiệm. Tuy nhi&ecirc;n, theo &ocirc;ng Biegun, Mỹ đ&atilde; bị phản bội.</p>\n<p>\"Đ&ocirc;i khi, ch&uacute;ng ta kh&oacute; từ bỏ những kết quả trong ước mơ. Nhiều người vẫn chưa từ bỏ. Ch&iacute;nh phủ n&agrave;y đang bị chỉ tr&iacute;ch nhiều bằng nhau cả khi h&agrave;nh động qu&aacute; bất ngờ hay qu&aacute; cứng rắn hoặc được cho rằng đang th&uacute;c đẩy Chiến tranh Lạnh mới. Đ&oacute; kh&ocirc;ng phải l&agrave; &yacute; định của ch&uacute;ng t&ocirc;i\", &ocirc;ng cho biết.</p>\n<p>Đốt t&agrave;i liệu ở l&atilde;nh sự qu&aacute;n TQ bị Mỹ y&ecirc;u cầu đ&oacute;ng cửa</p>\n<p>Cảnh s&aacute;t v&agrave; lực lượng cứu hỏa Mỹ c&oacute; mặt b&ecirc;n ngo&agrave;i Tổng l&atilde;nh sự qu&aacute;n Trung Quốc ở Houston tối 21/7 khi c&aacute;c nh&acirc;n vi&ecirc;n ngoại giao đang đốt t&agrave;i liệu ở b&ecirc;n trong.</p>',	'Trong khi Tổng thống Donald Trump tiếp tục quy trách nhiệm về đại dịch Covid-19 cho Trung Quốc, các quan chức cấp cao tại Washington đang mở rộng sức ép lên nhiều vấn đề khác.',	0,	2,	'2020-07-28 16:21:55',	NULL,	0,	3,	9),
(11,	'Giá xăng tăng hay giảm vào ngày mai?',	'<p>Dữ liệu từ Bộ C&ocirc;ng Thương cho biết gi&aacute; xăng th&agrave;nh phẩm b&igrave;nh qu&acirc;n tr&ecirc;n thị trường Singapore cập nhật đến ng&agrave;y 22/7 kh&ocirc;ng thay đổi nhiều so với kỳ t&iacute;nh gi&aacute; ng&agrave;y 13/7.</p>\n<p>Cụ thể, gi&aacute; xăng RON 92 d&ugrave;ng để pha chế xăng E5 RON 92 trung b&igrave;nh 44,77 USD/th&ugrave;ng, xăng RON 95 l&agrave; 46,31 USD/th&ugrave;ng. Gi&aacute; dầu trung b&igrave;nh tăng nhẹ.</p>\n<p style=\"text-align: center;\"><img title=\"Bai9_1.jpg\" src=\"/public/images/post_11_1.jpg\" alt=\"\" width=\"660\" height=\"408\" /></p>\n<p>Trao đổi với Zing, l&atilde;nh đạo một doanh nghiệp đầu mối ở TP.HCM cho biết gi&aacute; xăng thế giới những ng&agrave;y qua biến động kh&ocirc;ng lớn. Nếu kh&ocirc;ng t&iacute;nh quỹ b&igrave;nh ổn, gi&aacute; xăng E5 RON 92 c&oacute; thể chỉ tăng 20 đồng/l&iacute;t, trong khi gi&aacute; xăng RON 95 giảm 10 đồng/l&iacute;t.</p>\n<p>Tuy nhi&ecirc;n, vị chuy&ecirc;n gia nghi&ecirc;ng về phương &aacute;n cơ quan quản l&yacute; sẽ t&aacute;c động đến quỹ b&igrave;nh ổn xăng dầu. Theo đ&oacute;, việc gi&aacute; xăng tăng hay giảm, v&agrave; tăng giảm như thế n&agrave;o phụ thuộc v&agrave;o mức chi sử dụng quỹ b&igrave;nh ổn của cơ quan quản l&yacute;.</p>\n<p>Ở kỳ điều h&agrave;nh gi&aacute; xăng gần nhất l&agrave; ng&agrave;y 13/7, li&ecirc;n Bộ T&agrave;i ch&iacute;nh - C&ocirc;ng Thương quyết định giữ nguy&ecirc;n gi&aacute; c&aacute;c mặt h&agrave;ng xăng. Gi&aacute; b&aacute;n lẻ xăng RON 95 tr&ecirc;n thị trường tiếp tục ở mức 14.973 đồng/l&iacute;t, với xăng E5 RON 92 l&agrave; 14.258 đồng/l&iacute;t.</p>\n<p>Bộ C&ocirc;ng Thương mới đ&acirc;y tr&igrave;nh Ch&iacute;nh phủ dự thảo Nghị định sửa đổi, bổ sung một số điều của Nghị định số 83/2014/NĐ-CP ng&agrave;y 3/9/2014 về kinh doanh xăng dầu.</p>\n<p>Tại dự thảo Nghị định, Bộ C&ocirc;ng Thương đề xuất thời gian giữa hai lần điều chỉnh gi&aacute; xăng dầu li&ecirc;n tiếp l&agrave; 10 ng&agrave;y hoặc giữ nguy&ecirc;n l&agrave; 15 ng&agrave;y. Trường hợp ng&agrave;y điều h&agrave;nh gi&aacute; l&agrave; ng&agrave;y nghỉ hoặc ng&agrave;y nghỉ lễ theo quy định, Bộ C&ocirc;ng Thương thực hiện điều h&agrave;nh gi&aacute; v&agrave;o ng&agrave;y đầu ti&ecirc;n sau ng&agrave;y nghỉ hoặc ng&agrave;y nghỉ lễ của chu kỳ t&iacute;nh gi&aacute;.</p>\n<p>Trường hợp gi&aacute; cơ sở c&aacute;c mặt h&agrave;ng xăng dầu tăng cao bất thường so với gi&aacute; cơ sở kỳ liền trước đ&oacute;, ảnh hưởng lớn đến kinh tế, x&atilde; hội, Bộ C&ocirc;ng Thương c&oacute; tr&aacute;ch nhiệm b&aacute;o c&aacute;o Thủ tướng xem x&eacute;t quyết định thời gian điều h&agrave;nh gi&aacute; cụ thể cho kỳ điều h&agrave;nh đ&oacute;.</p>',	'Giá xăng thành phẩm không biến động nhiều so với kỳ tính giá ngày 13/7. Chuyên gia cho rằng giá xăng tăng hay giảm phụ thuộc vào mức chi sử dụng quỹ bình ổn của cơ quan quản lý.',	1,	2,	'2020-07-28 16:27:19',	NULL,	0,	3,	16),
(12,	'Học sinh trở lại trường, Thiên Long không còn thua lỗ',	'<p>Nhờ sức ti&ecirc;u thụ tăng mạnh v&agrave;o cuối qu&yacute; II, Thi&ecirc;n Long đ&atilde; b&ugrave; được khoản lỗ trong 4 th&aacute;ng đầu năm v&agrave; c&oacute; lợi nhuận 17 tỷ đồng sau 6 th&aacute;ng.</p>\n<p>C&ocirc;ng ty Cổ phần Tập đo&agrave;n Thi&ecirc;n Long, doanh nghiệp đứng đầu thị trường b&uacute;t viết, văn ph&ograve;ng phẩm tr&ecirc;n cả nước đ&atilde; c&oacute; l&atilde;i trở lại trong qu&yacute; II sau qu&yacute; I thua lỗ lần đầu trong lịch sử v&igrave; học sinh nghỉ học d&agrave;i ng&agrave;y để ph&ograve;ng chống dịch Covid-19.</p>\n<p>3 th&aacute;ng vừa qua, doanh thu thuần của Thi&ecirc;n Long l&agrave; 645 tỷ đồng, giảm hơn 30% so với c&ugrave;ng kỳ năm trước. Tỷ suất lợi nhuận gộp của doanh nghiệp cũng giảm từ 44,1% xuống 33,5%.</p>\n<p>Trong bối cảnh nguồn thu v&agrave; l&atilde;i gộp đều sụt giảm mạnh, Thi&ecirc;n Long đ&atilde; tiết giảm tổng chi ph&iacute; t&agrave;i ch&iacute;nh, b&aacute;n h&agrave;ng v&agrave; quản l&yacute; doanh nghiệp 12% c&ograve;n 178 tỷ. Sau khi hạch to&aacute;n chi ph&iacute; thuế, lợi nhuận r&ograve;ng của doanh nghiệp l&agrave; 37 tỷ, chỉ bằng 24% so với qu&yacute; II/2019.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_12_1.png\" width=\"715\" height=\"470\" /></p>\n<p>Lũy kế 6 th&aacute;ng đầu 2020, Thi&ecirc;n Long đạt doanh thu 1.113 tỷ đồng, giảm 28% so với c&ugrave;ng kỳ năm trước. Tuy nhi&ecirc;n, l&atilde;i sau thuế giảm tới 91% chỉ c&ograve;n 17 tỷ đồng.</p>\n<p>Năm nay, &ocirc;ng lớn ng&agrave;nh văn ph&ograve;ng phẩm đặt mục ti&ecirc;u doanh thu 2.800 tỷ v&agrave; lợi nhuận sau thuế 220 tỷ đồng, thấp hơn kết quả 2019 do ảnh hưởng từ dịch bệnh Covid-19. So với kế hoạch năm, Thi&ecirc;n Long ho&agrave;n th&agrave;nh 40% chỉ ti&ecirc;u doanh thu v&agrave; 8% chỉ ti&ecirc;u lợi nhuận sau 1/2 chặng đường.</p>\n<p>Tổng t&agrave;i sản của Thi&ecirc;n Long đến cuối th&aacute;ng 6 hơn 2.250 tỷ đồng, giảm gần 170 tỷ so với c&ugrave;ng kỳ. C&aacute;c khoản mục biến động mạnh tr&ecirc;n bảng c&acirc;n đối kế to&aacute;n của c&ocirc;ng ty gồm h&agrave;ng tồn kho tăng th&ecirc;m 100 tỷ v&agrave; khoản đầu tư t&agrave;i ch&iacute;nh ngắn hạn, cụ thể l&agrave; tiền gửi ở ng&acirc;n h&agrave;ng giảm 280 tỷ đồng.</p>\n<p>Giải tr&igrave;nh về biến động kết quả kinh doanh 6 th&aacute;ng đầu năm, ban l&atilde;nh đạo Thi&ecirc;n Long cho biết yếu tố ảnh hưởng lớn nhất l&ecirc;n c&ocirc;ng ty l&agrave; việc c&aacute;c trường học đ&oacute;ng cửa trong thời gian d&agrave;i để gi&atilde;n c&aacute;ch x&atilde; hội dưới t&aacute;c động của dịch Covid-19.</p>\n<p>Đối với mảng xuất khẩu, nhiều quốc gia l&agrave; thị trường trọng điểm tại ch&acirc;u &Aacute; tạm ngừng th&ocirc;ng quan h&agrave;ng h&oacute;a, nhiều nước thực hiện gi&atilde;n c&aacute;ch x&atilde; hội n&ecirc;n hoạt động b&aacute;n h&agrave;ng tại nước ngo&agrave;i diễn ra rất chậm. D&ugrave; vậy, c&ocirc;ng ty đ&atilde; cố gắng tăng th&ecirc;m số lượng, chủng loại sản phẩm v&agrave; kh&aacute;ch h&agrave;ng n&ecirc;n doanh số xuất khẩu 6 th&aacute;ng đầu năm chỉ giảm 6%, đạt 255 tỷ đồng.</p>\n<p>Doanh nghiệp đ&atilde; cố gắng tiết giảm chi ph&iacute;, gia tăng hiệu quả c&ocirc;ng việc nhưng v&igrave; sản lượng ti&ecirc;u thụ thấp n&ecirc;n đến th&aacute;ng 5 mới bắt đầu h&ograve;a vốn vận h&agrave;nh. Nhờ sức mua cải thiện đ&aacute;ng kể v&agrave;o cuối qu&yacute; II, Thi&ecirc;n Long đ&atilde; b&ugrave; được khoản lỗ trong 4 th&aacute;ng đầu năm v&agrave; c&oacute; lợi nhuận sau 6 th&aacute;ng.</p>\n<p>Ban l&atilde;nh đạo doanh nghiệp đứng đầu thị trường văn ph&ograve;ng phẩm cho biết đang tăng tốc chuẩn bị cho m&ugrave;a ti&ecirc;u thụ sắp tới bằng việc ph&acirc;n phối c&aacute;c sản phẩm thương hiệu ngoại của đối t&aacute;c Mỹ Newell Brand; tung c&aacute;c sản phẩm mới như x&agrave; ph&ograve;ng kh&ocirc; dạng thỏi, g&ocirc;m tẩy kh&aacute;ng khuẩn, m&aacute;y phun gel rửa tay tự động; l&agrave;m mới quầy kệ, bảng hiệu tr&ecirc;n khắp hệ thống ph&acirc;n phối cả nước.</p>',	'Nhờ sức tiêu thụ tăng mạnh vào cuối quý II, Thiên Long đã bù được khoản lỗ trong 4 tháng đầu năm và có lợi nhuận 17 tỷ đồng sau 6 tháng.',	0,	2,	'2020-07-28 16:30:25',	NULL,	0,	3,	16),
(13,	'4 mô hình iPhone 12 xuất hiện ở Việt Nam',	'<p>M&ocirc; h&igrave;nh iPhone 12 về H&agrave; Nội, vu&ocirc;ng vức giống iPhone 5S Theo m&ocirc; h&igrave;nh n&agrave;y, cả 4 model đều c&oacute; thiết kế giống nhau với phần cạnh được l&agrave;m vu&ocirc;ng vức tương</p>\n<p>&nbsp;tự iPhone 4, iPhone 5S hay iPad Pro.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_13_1.jpg\" /></p>\n<p>C&agrave;ng gần ng&agrave;y ra mắt, những h&igrave;nh ảnh, th&ocirc;ng tin về iPhone mới xuất hiện c&agrave;ng nhiều. Năm nay, nhiều th&ocirc;ng tin r&ograve; rỉ cho rằng Apple sẽ giới thiệu 4 mẫu iPhone mới bao gồm iPhone 12,</p>\n<p>&nbsp;iPhone 12 Max, iPhone 12 Pro v&agrave; iPhone 12 Pro Max.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_13_2.jpg\" /></p>\n<p>Theo m&ocirc; h&igrave;nh n&agrave;y, cả 4 model đều c&oacute; thiết kế giống nhau với phần cạnh được l&agrave;m vu&ocirc;ng vức tương tự iPhone 4, iPhone 5S hay iPad Pro. V&igrave; thế, khi cầm bản m&ocirc; h&igrave;nh iPhone 12, t&ocirc;i c&oacute; cảm gi&aacute;c cấn tay nhẹ,&nbsp;</p>\n<p>kh&ocirc;ng qu&aacute; thoải m&aacute;i như iPhone 11 với khung viền bo tr&ograve;n.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_13_3.jpg\" /></p>\n<p>&ldquo;Những mẫu m&ocirc; h&igrave;nh được sản xuất phục vụ nhu cầu trưng b&agrave;y v&agrave; l&agrave;m khu&ocirc;n mẫu tạo ra ốp lưng, miếng d&aacute;n&nbsp;</p>\n<p>m&agrave;n h&igrave;nh ch&agrave;o đ&oacute;n sản phẩm mới của chuỗi cửa h&agrave;ng&rdquo;, đại diện một hệ thống lớn ở H&agrave; Nội cho biết.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_13_4.jpg\" /></p>\n<p>L&agrave; một trong những nguồn r&ograve; rỉ th&ocirc;ng tin uy t&iacute;n, iHacktu cho biết iPhone 12 dự kiến c&oacute; 4 phi&ecirc;n bản, gồm 2 phi&ecirc;n bản ti&ecirc;u chuẩn với m&agrave;n h&igrave;nh 5,4 inch,&nbsp;</p>\n<p>6,1 inch v&agrave; 2 phi&ecirc;n bản Pro m&agrave;n h&igrave;nh 6,1 inch, 6,7 inch. Ở mặt trước của m&ocirc; h&igrave;nh vẫn c&oacute; phần khuyết đỉnh. C&aacute;c th&ocirc;ng tin r&ograve; rỉ cho rằng phần notch được l&agrave;m</p>\n<p>&nbsp;nhỏ gọn hơn, Face ID c&oacute; thể nhận diện khu&ocirc;n mặt</p>\n<p>&nbsp;ở nhiều g&oacute;c độ. Ri&ecirc;ng bộ đ&ocirc;i iPhone 12 Pro c&oacute; thể trang bị m&agrave;n h&igrave;nh ProMotion 120 Hz.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_13_5.jpg\" /></p>\n<p>Trong 4 mẫu mockup, c&oacute; 2 sản phẩm được trang bị camera k&eacute;p v&agrave; 2 model d&ugrave;ng 3 camera sau. iPhone mới phi&ecirc;n bản cao cấp c&oacute; thể được trang bị nhiều camera sau hơn.</p>\n<p><img src=\"/public/images/post_13_6.jpg\" /></p>\n<p>C&aacute;ch bố tr&iacute; c&aacute;c ph&iacute;m chức năng ở bản m&ocirc; h&igrave;nh tương tự iPhone 11. Theo đ&oacute;, b&ecirc;n cạnh phải c&oacute; ph&iacute;m nguồn, ph&iacute;m tăng, giảm &acirc;m lượng v&agrave; v&agrave; cần gạt mute nằm b&ecirc;n cạnh tr&aacute;i. Cả 4 m&ocirc; h&igrave;nh đều c&oacute; mặt lưng được l&agrave;m từ k&iacute;nh v&agrave; khung viền bằng th&eacute;p kh&ocirc;ng gỉ.</p>\n<p>Ở ph&iacute;a dưới c&oacute; loa ngo&agrave;i v&agrave; cổng sạc. Tuy nhi&ecirc;n vẫn chưa thể x&aacute;c định, Apple sẽ tiếp tục d&ugrave;ng cổng sạc lightning hay chuyển sang d&ugrave;ng cổng sạc USB-C.</p>\n<p>Ng&agrave;y 24/7, t&agrave;i khoản Twitter t&ecirc;n iHacktu bất ngờ cho biết Apple c&oacute; thể sẽ ra mắt iPhone 12 v&agrave;o ng&agrave;y 8/9. T&agrave;i khoản tr&ecirc;n cũng tiết lộ sự kiện giới thiệu sản phẩm tiếp theo sẽ diễn ra v&agrave;o ng&agrave;y 27/10.</p>',	'Mô hình được cho là iPhone 12 vừa xuất hiện tại một hệ thống smartphone lớn ở Hà Nội.  Theo đại diện cửa hàng, mô hình này được xách tay từ Trung Quốc.',	1,	2,	'2020-07-29 13:48:44',	NULL,	2,	3,	18),
(14,	'Khi ông Trump \'tổng lực\' tấn công TikTok',	'<p>TikTok, một ứng dụng đăng tải video nổi tiếng to&agrave;n cầu, đang l&agrave; &ldquo;ng&ocirc;i sao&rdquo; lớn trong l&agrave;ng giải tr&iacute; trực tuyến khi sở hữu hơn</p>\n<p>&nbsp;2 tỷ lượt tải xuống v&agrave; phủ s&oacute;ng tr&ecirc;n 155 quốc gia.</p>\n<p>Tuy nhi&ecirc;n, c&aacute;i m&aacute;c &ldquo;Made in China&rdquo; của TikTok khiến ứng dụng n&agrave;y kh&oacute; tr&aacute;nh khỏi tầm ngắm của nhiều quốc gia, trong đ&oacute; c&oacute; ch&iacute;nh</p>\n<p>&nbsp;quyền Tổng thống Donald Trump.</p>\n<p>Tại Mỹ, người d&acirc;n đang bất đắc dĩ phải chọn giữa TikTok hay tổng thống. Sự ủng hộ của người d&acirc;n d&agrave;nh cho một trong hai b&ecirc;n sẽ quyết định</p>\n<p>&nbsp;việc TikTok c&oacute; bị cấm tại quốc gia n&agrave;y hay kh&ocirc;ng.</p>\n<p>Tổng thống Trump c&oacute; lợi thế &ldquo;s&acirc;n nh&agrave;&rdquo;</p>\n<p>Năm 2019, TikTok đ&atilde; phải đối mặt với sự gi&aacute;m s&aacute;t của ch&iacute;nh phủ Mỹ do những lo ngại ảnh hưởng đến an ninh quốc gia.</p>\n<p>Tuy c&oacute; trụ sở tại Mỹ, CEO người Mỹ, TikTok vẫn kh&ocirc;ng thể phủ nhận nguồn gốc l&agrave; một c&ocirc;ng ty Trung Quốc khi thuộc sở hữu của ByteDance.</p>\n<p>Ch&iacute;nh l&yacute; do n&agrave;y đ&atilde; khiến những lo ngại TikTok chia sẻ dữ liệu người d&ugrave;ng cho ch&iacute;nh phủ Trung Quốc l&agrave; c&oacute; cơ sở. Kh&ocirc;ng chỉ ri&ecirc;ng Mỹ, giới c&ocirc;ng nghệ đ&atilde; kh&ocirc;ng &iacute;t lần tố c&aacute;o ứng dụng n&agrave;y c&oacute; h&agrave;nh vi thu thập dữ liệu chưa r&otilde; mục đ&iacute;ch.</p>\n<p>Theo Bloomberg, cuộc chiến chống lại TikTok của &ocirc;ng Trump đ&atilde; được đẩy sang một h&igrave;nh thức mới, th&ocirc;ng qua c&aacute;c quảng c&aacute;o tr&ecirc;n mạng x&atilde; hội Facebook v&agrave; Instagram.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_14_1.jpg\" /></p>\n<p>Bắt đầu từ h&ocirc;m 17/7, một chiến dịch quảng c&aacute;o tr&ecirc;n 2 nền tảng mạng x&atilde; hội đ&atilde; được nh&oacute;m tranh cử của Tổng thống Trump tung ra với mục đ&iacute;ch c&ocirc;ng k&iacute;ch TikTok. Nội dung quảng c&aacute;o cảnh b&aacute;o người d&ugrave;ng việc ứng dụng tạo video n&agrave;y c&oacute; nhiều hoạt động theo d&otilde;i người d&ugrave;ng.</p>\n<p>Kh&ocirc;ng dừng lại ở đ&oacute;, ph&oacute;ng vi&ecirc;n Taylor Lorenz của tờ Bloomberg đ&atilde; ph&aacute;t hiện t&iacute;nh chất thực sự của chiến dịch quảng c&aacute;o n&agrave;y l&agrave; một cuộc khảo s&aacute;t, y&ecirc;u cầu người d&ugrave;ng cung cấp t&ecirc;n, m&atilde; v&ugrave;ng, email v&agrave; số điện thoại.</p>\n<p>Sau đ&oacute;, những th&ocirc;ng tin được thu thập sẽ được th&ecirc;m v&agrave;o danh s&aacute;ch nhận thư điện tử cổ vũ tranh cử của chiến dịch Trump. Mục ti&ecirc;u của những quảng c&aacute;o n&agrave;y hướng đến l&agrave; người d&ugrave;ng từ 18-64 tuổi.</p>\n<p>Đầu th&aacute;ng 7, ch&iacute;nh quyền &ocirc;ng Trump tuy&ecirc;n bố sẽ xem x&eacute;t c&aacute;c khả năng cấm TikTok hoạt động tại Mỹ.</p>\n<p>Theo quan điểm của &ocirc;ng Trump, lệnh cấm sẽ l&agrave; c&aacute;ch để trừng phạt Trung Quốc do những li&ecirc;n quan đến đại dịch Covid-19. Trong khi đ&oacute;, Ngoại trưởng Mỹ Mike Pompeo v&agrave; giới chức Mỹ viện dẫn những lo ngại về an ninh quốc gia.</p>\n<p>Ngo&agrave;i ra, theo Politico, ng&agrave;y 20/7, Hạ viện Mỹ đ&atilde; bỏ phiếu cấm nh&acirc;n vi&ecirc;n li&ecirc;n bang tải TikTok, đồng thời hạn chế sự c&aacute;c nền tảng mạng x&atilde; hội tr&ecirc;n thiết bị do ch&iacute;nh phủ cung cấp.</p>\n<p>Với số phiếu thuận chiếm &aacute;p đảo 336 phiếu tr&ecirc;n tổng 407 phiếu ủng hộ Đạo luật Ủy quyền Quốc ph&ograve;ng, số phận của TikTok tại Mỹ đang bị đe dọa.</p>\n<p>Nếu &ocirc;ng Trump lấy được sự ủng hộ của người d&acirc;n v&agrave; loại bỏ TikTok ra khỏi thị trường Mỹ, đ&acirc;y c&oacute; thể l&agrave; yếu tố quan trọng ảnh hưởng đến cuộc t&aacute;i tranh cử Tổng thống diễn ra sắp tới.</p>\n<p>Tuy nhi&ecirc;n, với 165 triệu lượt tải về, l&agrave; thị trường đứng thứ 3 thế giới, việc cấm cửa TikTok tại Mỹ c&oacute; thể l&agrave; &ldquo;con dao hai lưỡi&rdquo; với những tham vọng của Tổng thống Trump.</p>\n<p>TikTok chi tiền để l&ocirc;i k&eacute;o người d&ugrave;ng</p>\n<p>Trong động th&aacute;i mới nhất của TikTok, ứng dụng n&agrave;y đ&atilde; c&ocirc;ng bố Quỹ s&aacute;ng tạo trị gi&aacute; 200 triệu USD nhằm hỗ trợ c&aacute;c nh&agrave; s&aacute;ng tạo tại Mỹ. Động th&aacute;i n&agrave;y được đ&aacute;nh gi&aacute; l&agrave; để cải thiện chất lượng nội dung c&aacute;c video tr&ecirc;n nền tảng.</p>\n<p>Trả lời v&agrave;o h&ocirc;m 23/7, ByteDance, c&ocirc;ng ty mẹ của TikTok cho biết chương tr&igrave;nh sẽ bắt đầu v&agrave;o th&aacute;ng 8. Ban đầu, chương tr&igrave;nh n&agrave;y sẽ nhắm mục ti&ecirc;u đến những c&aacute; nh&acirc;n c&oacute; ảnh hưởng lớn như gi&aacute;o vi&ecirc;n, streamer (người ph&aacute;t trực tuyến) v&agrave; gi&uacute;p đỡ c&aacute;c nh&agrave; s&aacute;ng tạo hợp t&aacute;c với những thương hiệu lớn.</p>\n<p><img src=\"/public/images/post_14_2.png\" /></p>\n<p>Y&ecirc;u cầu duy nhất l&agrave; c&aacute;c nh&agrave; s&aacute;ng tạo nội dung phải tr&ecirc;n 18 tuổi, c&oacute; lượng b&agrave;i đăng li&ecirc;n tục v&agrave; số lượng người theo d&otilde;i đạt chuẩn.</p>\n<p>Từ l&acirc;u, TikTok đ&atilde; bị chỉ tr&iacute;ch l&agrave; thiếu phần thưởng cho người s&aacute;ng tạo nội dung, kh&ocirc;ng giống như Youtube, cho ph&eacute;p người d&ugrave;ng kiếm tiền trực tiếp th&ocirc;ng qua nền tảng.</p>\n<p>Theo b&agrave; Vanessa Pappas, Gi&aacute;m đốc TikTok tại Mỹ, quỹ s&aacute;ng lập n&agrave;y sẽ c&oacute; t&aacute;c dụng gi&uacute;p người d&ugrave;ng nhận ra những lợi &iacute;ch khi sử dụng ứng dụng, đồng thời &ldquo;khuyến kh&iacute;ch những người c&oacute; kh&aacute;t vọng khơi dậy sự nghiệp truyền cảm hứng&rdquo;.</p>\n<p>Kể từ đầu năm 2020, TikTok đ&atilde; c&oacute; nhiều lần b&agrave;y tỏ &yacute; định &ldquo;tho&aacute;t Trung&rdquo;, tr&aacute;nh xa c&aacute;c yếu tố ch&iacute;nh trị v&agrave; n&acirc;ng cao hợp t&aacute;c.</p>\n<p>Theo Reuters, ByteDance đang &acirc;m thầm thực hiện c&aacute;c kế hoạch di chuyển nh&acirc;n sự ra khỏi \"đất nước tỷ d&acirc;n\" v&agrave; nhanh ch&oacute;ng mở rộng đội ngũ kỹ sư tại trụ sở Mỹ.</p>\n<p>Ngo&agrave;i ra, chia sẻ với CNBC, TikTok c&oacute; kế hoạch tuyển 10.000 lao động tại Mỹ trong 3 năm tới với mức lương hợp l&yacute;.</p>\n<p>T&iacute;nh tới nay, TikTok đ&atilde; thiệt hại h&agrave;ng tỷ USD khi để \"tuột\" mất thị trường Ấn Độ. Nếu ch&iacute;nh phủ Mỹ thực thi c&aacute;c biện ph&aacute;p loại bỏ ho&agrave;n to&agrave;n TikTok, ứng dụng n&agrave;y sẽ ch&iacute;nh thức đ&aacute;nh mất 2 thị trường lớn nhất của c&ocirc;ng ty.</p>',	'Người dân Mỹ đang đứng giữa cuộc chiến giữa chính quyền ông Trump và ứng dụng quay video nổi tiếng TikTok.',	0,	2,	'2020-07-29 13:53:26',	NULL,	1,	3,	19),
(15,	'Những võ sĩ quyền anh có tỷ lệ knock-out cao nhất lịch sử',	'<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_15_1.jpg\" /></p>\n<p>Edwin Valero (27/27, 100 %). Cố v&otilde; sĩ người Venezuela thi đấu quyền anh từ năm 2002 đến 2010. Valero từng v&ocirc; địch thế giới 2 hạng c&acirc;n, giữ đai WBA hạng si&ecirc;u l&ocirc;ng từ năm 2006 đến 2008 v&agrave; đai WBC hạng nhẹ từ năm 2009 đến 2010. Valero nổi tiếng&nbsp;</p>\n<p>với phong c&aacute;ch chiến đấu mạnh mẽ v&agrave; nắm đấm đầy sức mạnh. Sau 27 trận thượng đ&agrave;i, anh to&agrave;n thắng knock-out. Ảnh: AFP.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_15_2.jpg\" /></p>\n<p>Deontay Wilder (41/42, 95 %). Trước 2 lần so găng với Tyson Fury, cựu v&ocirc; địch WBC hạng nặng c&oacute; 42 trận bất bại v&agrave; 41 chiến thắng knock-out. Năm 2018, Wilder đối đầu Fury lần thứ nhất, nhưng bất ph&acirc;n thắng bại. Sau 2 năm, v&otilde; sĩ người Mỹ thua ở trận t&aacute;i đấu. Wilder được biết đến l&agrave; một trong những v&otilde; sĩ quyền anh c&oacute; c&uacute; đấm mạnh nhất. Ảnh: Reuters.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_15_3.jpg\" /></p>\n<p>George Foreman (68/76, 89 %). Cựu v&otilde; sĩ người Mỹ từng 2 lần v&ocirc; địch thế giới hạng nặng v&agrave; gi&agrave;nh huy chương v&agrave;ng Olympic. Ở tuổi 45, Foreman trở th&agrave;nh nh&agrave; v&ocirc; địch hạng nặng lớn tuổi nhất lịch sử quyền anh, sau khi gi&agrave;nh chiến thắng trước Michael Moorer v&agrave;o năm 1994. Ảnh: AFP.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_15_4.jpg\" /></p>\n<p>Mike Tyson (44/50, 88 %). Huyền thoại người Mỹ từng c&oacute; 19 chiến thắng knock-out li&ecirc;n tiếp trước khi trở th&agrave;nh nh&agrave; v&ocirc; địch hạng nặng trẻ nhất lịch sử ở tuổi 20 v&agrave;o năm 1986. Tyson nổi tiếng với c&uacute; đấm khiến nhiều đối thủ khiếp sợ. Năm 2005, &ocirc;ng tuy&ecirc;n bố giải nghệ. Gần đ&acirc;y, \"Tay đấm th&eacute;p\" th&ocirc;ng b&aacute;o trở lại s&agrave;n đấu ở tuổi 53. Ảnh: Getty.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_15_5.jpg\" /></p>\n<p>Vitali Klitschko (41/45, 87 %). Cựu v&otilde; sĩ người Ukraina l&agrave; anh trai của Wladimir Klitschko. Kh&aacute;c với lối đ&aacute;nh kỹ thuật của Wladimir, Vitali sở hữu c&uacute; đấm uy lực v&agrave; lối đ&aacute;nh mạnh mẽ. Anh l&agrave; c&oacute; 3 lần v&ocirc; địch thế giới hạng nặng v&agrave; giữ danh hiệu WBC l&acirc;u nhất mọi thời đại.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_15_6.jpg\" /></p>\n<p>Naoya Inoue (16/19, 84 %). V&otilde; sĩ người Nhật Bản l&agrave; nh&agrave; v&ocirc; địch thế giới 3 hạng c&acirc;n v&agrave; hiện nắm giữ danh hiệu hạng g&agrave;. Inoue nổi tiếng với c&uacute; đấm c&oacute; lực s&aacute;t thương cao. Đ&acirc;y l&agrave; một trong những l&yacute; do anh c&oacute; biệt danh l&agrave; \"qu&aacute;i vật\". Ảnh: Getty.</p>',	'Một số võ sĩ được đánh giá dựa trên sức mạnh của nắm đấm, không phải danh hiệu thế giới, kỷ lục hay kỹ năng mà họ sở hữu. Tỷ lệ knock-out được tính trên tổng trận thắng.',	1,	2,	'2020-08-17 00:00:20',	NULL,	0,	3,	23),
(16,	'Từ Hiểu Đông tự tin knock-out Ngô Kinh trong một phút',	'<p>\"Ng&ocirc; Kinh chỉ tập luyện v&otilde; thuật chứ kh&ocirc;ng chiến đấu thực sự. Nếu bước l&ecirc;n v&otilde; đ&agrave;i, t&ocirc;i c&oacute; thể knock-out anh ta chỉ trong một ph&uacute;t\", trang Hotbak dẫn lời của v&otilde; sĩ họ Từ.</p>\n<p>Hiểu Đ&ocirc;ng kh&ocirc;ng &iacute;t lần bu&ocirc;ng lời th&aacute;ch đấu v&agrave; tỏ ra xem thường c&aacute;c ng&ocirc;i sao v&otilde; thuật Trung Quốc. Năm 2017, anh c&ocirc;ng khai ch&ecirc; bai Ch&acirc;n Tử Đan v&agrave; kh&ocirc;ng đề cao huyền thoại L&yacute; Tiểu Long.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_16_1.jpg\" /></p>\n<p>Đ&aacute;p lại lời th&aacute;ch đấu của Hiểu Đ&ocirc;ng, Ng&ocirc; Kinh giữ im lặng. Khi được hỏi về trận đấu giữa v&otilde; sĩ họ Từ v&agrave; Nguỵ L&ocirc;i, anh thừa nhận kh&ocirc;ng quan t&acirc;m. Nhờ v&agrave;o v&otilde; thuật truyền thống, Ng&ocirc; Kinh trở th&agrave;nh ng&ocirc;i sao v&agrave; đạo diễn nổi tiếng. V&igrave; vậy, Ng&ocirc; Kinh kh&ocirc;ng ch&uacute; &yacute; đến bất kỳ ai l&agrave;m suy giảm danh tiếng của v&otilde; thuật truyền thống.</p>\n<p>Hiểu Đ&ocirc;ng v&agrave; Ng&ocirc; Kinh đều tốt nghiệp từ trường Đại học Thể thao Bắc Kinh, nhưng sự nổi tiếng v&agrave; ảnh hưởng của ng&ocirc;i sao họ Ng&ocirc; nhiều hơn Hiểu Đ&ocirc;ng. Tuy nhi&ecirc;n, trong những năm gần đ&acirc;y, v&otilde; sĩ họ Từ được quan t&acirc;m hơn khi li&ecirc;n tục đ&aacute;nh bại c&aacute;c cao thủ của v&otilde; thuật truyền thống.</p>\n<p>Khả năng chiến đấu của Hiểu Đ&ocirc;ng l&agrave; kh&ocirc;ng thể phủ nhận, nhưng t&iacute;nh c&aacute;ch hống h&aacute;ch của anh khi th&aacute;ch đấu v&otilde; thuật truyền thống kh&ocirc;ng được đ&aacute;nh gi&aacute; cao.</p>\n<p>V&otilde; sĩ họ Từ nhiều lần vạch trần thực lực của v&otilde; thuật truyền thống khi hạ gục 2 v&otilde; sư Th&aacute;i Cực quyền, Điền D&atilde; V&agrave; Nguỵ L&ocirc;i, c&ugrave;ng Đinh Hạo, người tự nhận l&agrave; truyền nh&acirc;n của Diệp Vấn.</p>',	'Võ sĩ MMA nghiệp dư Từ Hiểu Đông tiếp tục có những lời lẽ chê bai võ thuật truyền thống Trung Quốc và tuyên bố sẽ đánh bại ngôi sao Ngô Kinh trong 60 giây. ',	0,	2,	'2020-08-17 00:00:20',	NULL,	0,	3,	23),
(17,	'Trợ lý HLV đến bệnh viện ở Đà Nẵng, đội Quảng Nam phải cách ly',	'<p>Th&ocirc;ng tin được Gi&aacute;m đốc kỹ thuật Đo&agrave;n Ph&ugrave;ng của CLB Quảng Nam chia sẻ với Zing trong s&aacute;ng 28/7. Trong l&uacute;c CLB Đ&agrave; Nẵng đang thực hiện c&aacute;ch ly, th&igrave; đến lượt đội Quảng Nam phải c&aacute;ch ly sau khi trợ l&yacute; HLV Dương Văn Tuyến chủ động khai b&aacute;o đ&atilde; gh&eacute; thăm Bệnh viện C (Đ&agrave; Nẵng).</p>\n<p>\"Trợ l&yacute; HLV Văn Tuyến đ&atilde; đi thăm người nh&agrave; ở Bệnh viện C (Đ&agrave; Nẵng) ng&agrave;y 21/7 v&agrave; 25/7. Vừa qua anh ấy đ&atilde; khai b&aacute;o y tế địa phương v&agrave; trước mắt đang theo d&otilde;i c&aacute;ch ly tại nh&agrave;. Thời gian qua anh ấy đ&atilde; tiếp x&uacute;c gần với đội b&oacute;ng, n&ecirc;n to&agrave;n đội Quảng Nam cũng đang phải thực hiện c&aacute;ch ly ở đại bản doanh, nội bất xuất, ngoại bất nhập\", &ocirc;ng Ph&ugrave;ng chia sẻ.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_17_1.jpg\" /></p>\n<p>Việc to&agrave;n đội phải c&aacute;ch ly &iacute;t nhiều t&aacute;c động tới quyết định xin dừng V.League 2020 của Ban l&atilde;nh đạo CLB Quảng Nam. &Ocirc;ng Ph&ugrave;ng cho biết đội cũng mới gửi c&ocirc;ng văn xin dừng giải đấu, bởi đ&acirc;y đ&atilde; l&agrave; lần thứ 2 b&oacute;ng đ&aacute; chuy&ecirc;n nghiệp Việt Nam bị tạm ho&atilde;n v&agrave; chưa hẹn ng&agrave;y trở lại.</p>\n<p>\"L&uacute;c đ&oacute; chưa b&ugrave;ng ph&aacute;t dịch, cả đội sinh hoạt b&igrave;nh thường, n&ecirc;n ch&uacute;ng t&ocirc;i cũng kh&ocirc;ng biết g&igrave; cho đến khi anh Tuyến chủ động khai b&aacute;o. To&agrave;n đội Quảng Nam đ&atilde; đ&oacute;ng trại 100% từ h&ocirc;m 26/7, c&ugrave;ng thời điểm nhận th&ocirc;ng b&aacute;o ho&atilde;n V.League. Trước mắt, to&agrave;n đội Quảng Nam phải đợi 2 tuần, chờ c&aacute;ch ly xong rồi mới t&iacute;nh tiếp theo y&ecirc;u cầu của Ban chỉ đạo ph&ograve;ng chống dịch Covid-19. Thế n&ecirc;n, đội đề xuất xin dừng giải lu&ocirc;n\", GĐKT CLB Quảng Nam cho biết th&ecirc;m.</p>\n<p>T&iacute;nh đến l&uacute;c n&agrave;y, đ&atilde; c&oacute; 5 đội đồng t&igrave;nh với đề xuất xin VFF v&agrave; VPF hủy giải. Đ&oacute; l&agrave; Quảng Nam, SLNA, CLB Thanh H&oacute;a, CLB Nam Định v&agrave; CLB Hải Ph&ograve;ng. L&atilde;nh đạo đội b&oacute;ng đất cảng cho biết CLB cũng đồng t&igrave;nh với việc kết th&uacute;c sớm giải đấu sau 11 v&ograve;ng để tập trung c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch cũng như giảm thiểu g&aacute;nh nặng t&agrave;i ch&iacute;nh trong thời gian kh&ocirc;ng thi đấu.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_17_2.jpg\" /></p>',	'Toàn bộ thành viên CLB Quảng Nam bắt đầu quá trình tự cách ly từ ngày 26/7, ngay sau khi ghi nhận trường hợp một thành viên ban huấn luyện đến bệnh viện ở Đà Nẵng.',	1,	2,	'2020-08-17 00:00:20',	NULL,	0,	3,	22),
(18,	'Bố cầu thủ Vũ Văn Thanh qua đời',	'<p>Tiền vệ Vũ Văn Thanh đ&atilde; chuẩn bị sẵn t&acirc;m l&yacute; đ&oacute;n nhận sự chia ly. Bố Văn Thanh ra đi v&igrave; căn bệnh ung thư phổi v&agrave;o chiều 28/7 ở qu&ecirc; nh&agrave; Hải Dương.</p>\n<p>Nhiều bạn b&egrave;, người th&acirc;n của Văn Thanh đ&atilde; chia sẻ sự mất m&aacute;t c&ugrave;ng cầu thủ HAGL tr&ecirc;n mạng x&atilde; hội. Tiền đạo Nguyễn Tiến Linh viết: \"Chia buồn c&ugrave;ng anh v&agrave; gia đ&igrave;nh\". Trợ l&yacute; HLV Nguyễn Văn Đ&agrave;n cũng gửi lời chia buồn tới gia đ&igrave;nh Văn Thanh.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_18_1.jpg\" /></p>\n<p>Nhiều cầu thủ, đồng đội, huấn luyện vi&ecirc;n, ph&oacute;ng vi&ecirc;n để lại những chia sẻ với mất m&aacute;t của cầu thủ sinh năm 1996. Văn Thanh đ&atilde; phải n&eacute;n nỗi buồn để thi đấu trong thời gian qua. Anh li&ecirc;n tục đi đi, về về giữa địa điểm thi đấu v&agrave; Hải Dương để b&ecirc;n cạnh bố.</p>\n<p>Trước đ&oacute;, c&aacute;c cổ động vi&ecirc;n của Văn Thanh cũng ph&aacute;t động phong tr&agrave;o \"Be strong, Vũ Văn Thanh!\" với th&ocirc;ng điệp đồng h&agrave;nh c&ugrave;ng cầu thủ n&agrave;y trong thời gian người bố ở qu&ecirc; nh&agrave; đang l&acirc;m bệnh nặng. Sau trận đấu với Quảng Nam h&ocirc;m 17/7, hậu vệ HAGL cũng xin ph&eacute;p CLB về nh&agrave; thăm bố rồi trở lại s&aacute;t ng&agrave;y thi đấu v&ograve;ng 11 gặp Thanh H&oacute;a.</p>\n<p>Ở trận đấu với Quảng Nam, sau khi lập c&ocirc;ng tr&ecirc;n chấm phạt đền, Văn Thanh c&oacute; h&agrave;nh động nhắm mắt, chắp tay ngẩng mặt l&ecirc;n trời cầu nguyện cho sức khỏe của bố. Chia sẻ ở buổi họp b&aacute;o sau trận gặp Thanh H&oacute;a h&ocirc;m 23/7, HLV Lee Tae-hoon cho biết: \"T&ocirc;i muốn gửi lời cảm ơn tới Văn Thanh. T&ocirc;i biết sức khỏe bố cậu ấy giờ đang c&oacute; chuyển biến xấu, nhưng cậu ấy vẫn thể hiện sự chuy&ecirc;n nghiệp. Văn Thanh đ&atilde; thi đấu với tinh thần v&igrave; tập thể đội b&oacute;ng. Với tư c&aacute;ch tiền bối đi trước, t&ocirc;i cảm ơn Văn Thanh\".</p>\n<p>Ở trận đấu đ&oacute;, Văn Thanh thi đấu trọn vẹn 90 ph&uacute;t, chơi tập trung v&agrave; gi&uacute;p HAGL giữ sạch lưới để c&oacute; 1 điểm.</p>\n<p>\"T&ocirc;i đ&atilde; bảo Thanh cứ ở nh&agrave; sắp xếp c&ocirc;ng việc gia đ&igrave;nh đi, thế nhưng cậu ấy vẫn l&ecirc;n tập trung c&ugrave;ng c&aacute;c đồng đội. Với tư c&aacute;ch người đi trước v&agrave; HLV trưởng, t&ocirc;i cảm ơn v&agrave; mong Thanh sẽ ph&aacute;t triển nhiều hơn nữa trong tương lai\", HLV Lee n&oacute;i th&ecirc;m.</p>\n<p>Văn Thanh l&agrave; cầu thủ trưởng th&agrave;nh từ Học viện B&oacute;ng đ&aacute; HAGL-Arsenal JMG (giờ l&agrave; Học viện HAGL-JMG). Văn Thanh c&ugrave;ng lứa với C&ocirc;ng Phượng, Tuấn Anh, Xu&acirc;n Trường. Cầu thủ sinh năm 1996 l&agrave; th&agrave;nh vi&ecirc;n đội U23 Việt Nam gi&agrave;nh ng&ocirc;i &aacute; qu&acirc;n tại VCK U23 ch&acirc;u &Aacute; 2018 ở Thường Ch&acirc;u (Trung Quốc). Anh l&agrave; một trong những hậu vệ c&aacute;nh tr&aacute;i được HLV Park Hang-seo kỳ vọng. Văn Thanh vừa trở lại sau chấn thương đứt d&acirc;y chằng hồi th&aacute;ng 10/2018.</p>',	'Ông Vũ Văn Hộ trút hơi thở cuối cùng vào chiều ngày 28/7 vì bạo bệnh.Tiền vệ Vũ Văn Thanh đã chuẩn bị sẵn tâm lý đón nhận sự chia ly. Bố Văn Thanh ra đi vì căn bệnh ung thư phổi vào chiều 28/7 ở quê nhà Hải Dương.',	0,	2,	'2020-07-29 14:04:43',	NULL,	0,	3,	21),
(19,	'VFF sẽ xử lý trọng tài mắc sai sót',	'<p>&ldquo;Ch&uacute;ng t&ocirc;i sẽ xử l&yacute; những HLV, cầu thủ tham gia giải đấu nhưng kh&ocirc;ng tu&acirc;n thủ theo điều luật v&agrave; c&oacute; những lời chỉ tr&iacute;ch VFF v&agrave; Ban tổ chức. Bởi h&igrave;nh ảnh giải đấu l&agrave; điều rất quan trọng. Kể cả với &ocirc;ng Park, hợp đồng cũng quy định &ocirc;ng c&oacute; quyền b&igrave;nh luận c&aacute;c vấn đề chuy&ecirc;n m&ocirc;n nhưng kh&ocirc;ng được b&igrave;nh luận c&aacute;c ch&iacute;nh s&aacute;ch của VFF v&agrave; c&aacute;c vấn đề c&ograve;n đang tranh chấp&rdquo;, &ocirc;ng L&ecirc; Ho&agrave;i Anh chia sẻ.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_19_1.jpg\" /></p>\n<p>V.League 2020 l&agrave; m&ugrave;a giải quyết liệt bậc nhất của b&oacute;ng đ&aacute; Việt Nam trong những năm gần đ&acirc;y. B&ecirc;n cạnh sự quyết liệt của giải đấu, c&ocirc;ng t&aacute;c tổ chức giải đ&atilde; bị ảnh hưởng nghi&ecirc;m trọng do những sai s&oacute;t từ đội ngũ trọng t&agrave;i.</p>\n<p>C&aacute;c đội b&oacute;ng, c&aacute;c HLV, cầu thủ v&agrave; người h&acirc;m mộ đều đ&atilde; phản ứng quyết liệt trước c&ocirc;ng t&aacute;c trọng t&agrave;i. Nhiều ph&aacute;t ng&ocirc;n c&oacute; phần qu&aacute; kh&iacute;ch, thiếu t&iacute;nh x&acirc;y dựng đ&atilde; được đưa ra.</p>\n<p>VFF cho biết c&aacute;c trọng t&agrave;i mắc sai s&oacute;t sẽ bị xử l&yacute;. Tuy nhi&ecirc;n, việc c&aacute;c CLB phản ứng qu&aacute; kh&iacute;ch, quy chế b&oacute;ng đ&aacute; chuy&ecirc;n nghiệp cũng đ&atilde; c&oacute; quy định. C&aacute;c ph&aacute;t ng&ocirc;n nhắm v&agrave;o ban tổ chức giải v&agrave; VFF chỉ được n&ecirc;u trong c&aacute;c hội nghị r&uacute;t kinh nghiệm, c&aacute;c diễn đ&agrave;n phản &aacute;nh c&ocirc;ng khai hoặc văn bản ch&iacute;nh thức. Nếu đ&oacute; l&agrave; ph&aacute;t ng&ocirc;n trước c&ocirc;ng ch&uacute;ng hoặc truyền th&ocirc;ng chưa qua kiểm chứng, c&aacute;c ph&aacute;t ng&ocirc;n ấy sẽ ảnh hưởng nhiều tới h&igrave;nh ảnh giải đấu.</p>\n<p>Tổng thư k&yacute; VFF cũng khẳng định c&aacute;c đội b&oacute;ng phải t&ocirc;n trọng đơn vị điều h&agrave;nh giải đấu, trận đấu cả trong v&agrave; ngo&agrave;i s&acirc;n. &Ocirc;ng Ho&agrave;i Anh n&oacute;i: &ldquo;Khiếu nại của c&aacute;c đội trong s&acirc;n phải th&ocirc;ng qua đội trưởng đội b&oacute;ng. Nhưng với b&oacute;ng đ&aacute; Việt Nam, cầu thủ nhiều khi c&oacute; h&agrave;nh vi trực tiếp với trọng t&agrave;i. Họ sẽ bị xử l&yacute; bằng thẻ phạt. Những lỗi m&agrave; thẻ phạt chưa xử l&yacute; được sẽ được gi&aacute;m s&aacute;t trận đấu v&agrave; trọng t&agrave;i b&aacute;o c&aacute;o lại để xem x&eacute;t sau&rdquo;.</p>\n<p>Chia sẻ của &ocirc;ng L&ecirc; Ho&agrave;i Anh đến trong bối cảnh c&aacute;c đội b&oacute;ng, cầu thủ đang phản ứng rất dữ dội với trọng t&agrave;i. Nhiều CLB như TP.HCM xem &ldquo;qu&acirc;y&rdquo; trọng t&agrave;i l&agrave; một chiến thuật phản ứng trong c&aacute;c trận đấu.</p>',	'Tổng thư ký Lê Hoài Anh khẳng định Liên đoàn Bóng đá Việt Nam (VFF) sẽ nỗ lực đưa ra các giải pháp để góp phần tổ chức V.League, hạng Nhất tốt hơn.',	1,	2,	'2020-07-29 14:06:41',	NULL,	1,	3,	21),
(20,	'9 thần tượng Kpop thừa nhận chỉnh mũi',	'<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_20_1.jpg\" /></p>\n<p>JooE (Momoland): Từng bị coi l&agrave; \"thần tượng xấu nhất lịch sử Kpop\", JooE của hiện tại ng&agrave;y c&agrave;ng thu h&uacute;t. Trong một chương tr&igrave;nh năm 2018, JooE tiết lộ rằng c&ocirc; kh&ocirc;ng thể bấm lỗ tai v&igrave; đ&atilde; kh&ocirc;ng c&ograve;n sụn tai. Khi được hỏi nguy&ecirc;n nh&acirc;n cho điều n&agrave;y, JooE cho biết sụn tai hiện được gh&eacute;p tr&ecirc;n mũi m&igrave;nh.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_20_2.jpg\" /></p>\n<p>Jung Chaeyeon (DIA): Năm 2016, trong chương tr&igrave;nh Video Star (MBC), người dẫn chương tr&igrave;nh hỏi Chaeyeon rằng c&ocirc; c&oacute; phẫu thuật mắt hai m&iacute; kh&ocirc;ng. Nữ thần tượng giải th&iacute;ch rằng m&iacute; mắt của c&ocirc; l&agrave; tự nhi&ecirc;n, tuy nhi&ecirc;n c&ocirc; c&oacute; nhờ đến sự can thiệp thẩm mỹ tr&ecirc;n mũi. Chaeyeon được khen ngợi v&igrave; nhan sắc xứng danh \"nữ thần thế hệ mới Kpop\" d&ugrave; từng trải qua phẫu thuật.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_20_3.jpg\" /></p>\n<p>Heechul (Super Junior): Kh&aacute;c với những thần tượng kh&aacute;c, Heechul buộc phải phẫu thuật thẩm mỹ mũi v&igrave; vấn đề sức khoẻ. Trong thời gian phục vụ qu&acirc;n ngũ, anh gặp phải tai nạn l&agrave;m g&atilde;y mũi, Heechul nhờ cậy dao k&eacute;o để t&aacute;i tạo bộ phận n&agrave;y.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_20_4.jpg\" /></p>\n<p>Jessi: Vốn l&agrave; nghệ sĩ c&aacute; t&iacute;nh, Jessi kh&ocirc;ng ngại ngần tiết lộ qu&aacute; tr&igrave;nh phẫu thuật thẩm mỹ của bản th&acirc;n. Nữ rapper cho biết c&ocirc; đ&atilde; n&acirc;ng mũi, n&acirc;ng ngực v&agrave; cắt mắt hai m&iacute; để c&oacute; diện mạo thu h&uacute;t như mong muốn.</p>\n<p>H</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_20_5.jpg\" /></p>\n<p>Minzy (2NE1): V&igrave; Yang Hyun Suk - người s&aacute;ng lập YG Entertainment, lu&ocirc;n chuộng nhan sắc tự nhi&ecirc;n hơn phẫu thuật thẩm mỹ, Minzy từng ph&acirc;n v&acirc;n việc liệu c&ocirc; c&oacute; n&ecirc;n nhờ cậy dao k&eacute;o để cải thiện chiếc mũi của m&igrave;nh. Tuy nhi&ecirc;n, trong khi trải qua ca phẫu thuật để điều trị vi&ecirc;m mũi, c&ocirc; đ&atilde; nhận được sự ủng hộ của CEO v&agrave; quyết định sửa mũi để xinh xắn, tự tin.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_20_6.jpg\" /></p>\n<p>Soyou (Sistar): Kể từ khi ra mắt, Soyou phải nhận nhiều b&igrave;nh luận gh&eacute;t bỏ của kh&aacute;n giả. Nhiều người cho rằng c&ocirc; k&eacute;m sắc, mũi qu&aacute; to l&agrave;m xấu gương mặt. Cuối c&ugrave;ng, Soyou quyết định sửa mũi để tự tin hơn khi xuất hiện trước c&ocirc;ng ch&uacute;ng.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_20_7.jpg\" /></p>\n<p>Dongwan (Shinhwa): Dongwan l&agrave; một trong những ng&ocirc;i sao thẳng thắn thừa nhận phẫu thuật thẩm mỹ tại giới giải tr&iacute; H&agrave;n Quốc. Năm 2017, Dongwan n&oacute;i với kh&aacute;n giả trong chương tr&igrave;nh radio MC Mong&rsquo;s Pleasure and Pain rằng anh từng phẫu thuật mũi trước khi ra mắt v&igrave; gặp sự cố bị loa rơi tr&uacute;ng mặt.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_20_8.jpg\" /></p>\n<p>Seo In Young (Jewelry): Seo In Young từng tuy&ecirc;n bố với người h&acirc;m mộ c&ocirc; sẽ kh&ocirc;ng bao giờ phẫu thuật thẩm mỹ. Tuy nhi&ecirc;n, sau nhiều lần xuất hiện với chiếc mũi biến dạng, c&ocirc; đ&atilde; thừa nhận tr&ecirc;n b&aacute;o ch&iacute; m&igrave;nh đụng dao k&eacute;o ở mũi tới hai lần.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_20_9.jpg\" /></p>\n<p>Kim Yewon (Jewelry): Một th&agrave;nh vi&ecirc;n kh&aacute;c của Jewelry l&agrave; Kim Yewon cũng thừa nhận m&igrave;nh phẫu thuật thẩm mỹ. Trong chương tr&igrave;nh We Got Married năm 2015, c&ocirc; đ&atilde; th&uacute; nhận b&iacute; mật n&agrave;y với \"chồng hờ\" Henry sau khi một thầy b&oacute;i ph&aacute;t hiện ra c&ocirc; từng n&acirc;ng mũi.</p>',	'Theo Mydaily, nhiều thần tượng Kpop được khán giả khen ngợi vì thẳng thắn thừa nhận việc phẫu thuật thẩm mỹ mũi, bộ phận quan trọng trên gương mặt.',	0,	2,	'2020-07-29 14:09:57',	NULL,	1,	3,	25),
(21,	'VỤ NAM CA SĨ HÀN QUAY LÉN CẢNH NHẠY CẢM CỦA PHỤ NỮ BỊ PHÁT HIỆN RA SAO',	'<p>Th&aacute;ng 3/2019, ng&agrave;nh giải tr&iacute; ch&acirc;u &Aacute; r&uacute;ng động v&igrave; vụ việc nh&oacute;m chat t&igrave;nh dục của Seungri c&ugrave;ng Jung Joon Young, Choi Jong Hoon, Yoo In Suk. Truyền th&ocirc;ng li&ecirc;n tục đưa tin, dư luận H&agrave;n Quốc bất b&igrave;nh về h&agrave;nh vi quay l&eacute;n v&agrave; ph&aacute;t t&aacute;n cảnh quan hệ t&igrave;nh dục của c&aacute;c c&ocirc; g&aacute;i.</p>\n<p>Nhưng nh&oacute;m chat t&igrave;nh dục của Seungri chỉ l&agrave; bề nổi của tảng băng ch&igrave;m. Việc quay l&eacute;n phụ nữ vốn l&agrave; vấn nạn nghi&ecirc;m trọng l&acirc;u năm kh&ocirc;ng chỉ trong showbiz m&agrave; với cả x&atilde; hội H&agrave;n Quốc n&oacute;i chung.</p>\n<p>Từ vụ &aacute;n Seungri v&agrave; g&oacute;c khuất showbiz...</p>\n<p>Ban đầu, Seungri v&agrave; nh&oacute;m bạn bị điều tra v&igrave; chia sẻ h&igrave;nh ảnh nhạy cảm của nhiều phụ nữ. Sau khi đ&agrave;o s&acirc;u v&agrave;o loạt bằng chứng thu được, cơ quan chức năng H&agrave;n Quốc ph&aacute;t hiện kh&ocirc;ng chỉ ph&aacute;t t&aacute;n, nh&oacute;m chat của cựu th&agrave;nh vi&ecirc;n Big Bang c&ograve;n d&iacute;nh đến h&agrave;ng loạt vụ m&ocirc;i giới mại d&acirc;m, đặt m&aacute;y quay l&eacute;n cảnh quan hệ t&igrave;nh dục.</p>\n<p>Tại phi&ecirc;n t&ograve;a th&aacute;ng 5/2019, Jung Joon Young - th&agrave;nh vi&ecirc;n nh&oacute;m chat Seungri - đ&atilde; c&uacute;i đầu thừa nhận 11 tội danh quay phim v&agrave; ph&aacute;t t&aacute;n nội dung t&igrave;nh dục bất hợp ph&aacute;p qua nền tảng mạng x&atilde; hội. C&aacute;c thần tượng Choi Jong Hoon, Yong Jun Hyung cũng phải giải nghệ v&igrave; chia sẻ văn h&oacute;a phẩm đội truỵ, c&aacute;c video quay l&eacute;n.</p>\n<p>Chuỗi scandal t&igrave;nh dục li&ecirc;n ho&agrave;n của c&aacute;c \"bạch m&atilde; ho&agrave;ng tử Kpop\" khiến dư luận b&agrave;ng ho&agrave;ng, nhận ra mặt tối của ng&agrave;nh giải tr&iacute;. Kh&ocirc;ng chỉ c&oacute; trai xinh g&aacute;i đẹp, phim ảnh hay &acirc;m nhạc, showbiz H&agrave;n c&ograve;n tồn tại cả những g&oacute;c khuất về &aacute;m ảnh t&igrave;nh dục kh&ocirc;ng bao giờ biến mất.</p>\n<p>Chia sẻ với Korea Times, David Tizzard - trợ l&yacute; gi&aacute;o sư tại Đại học Nữ sinh Seoul - từng nhận định t&igrave;m kiếm v&agrave; chia sẻ video bất hợp ph&aacute;p của nhiều phụ nữ trẻ, kh&ocirc;ng &iacute;t trong số n&agrave;y c&oacute; thể bị cưỡng &eacute;p hoặc đ&aacute;nh thuốc m&ecirc;, l&agrave; một tr&ograve; ti&ecirc;u khiển quen thuộc của c&aacute;c nghệ sĩ nam. Nhiều người thậm ch&iacute; kh&ocirc;ng chỉ xem video tr&ecirc;n mạng m&agrave; c&ograve;n trực tiếp \"ra tay\" với ch&iacute;nh đồng nghiệp hay người h&acirc;m mộ của m&igrave;nh.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_21_1.jpg\" /></p>\n<p>Ng&agrave;y 1/6, truyền th&ocirc;ng H&agrave;n đưa tin nh&agrave; vệ sinh nữ của đ&agrave;i KBS bị đặt camera quay l&eacute;n. Thủ phạm trong vụ việc n&agrave;y l&agrave; nam diễn vi&ecirc;n h&agrave;i từng l&agrave;m việc cho chương tr&igrave;nh Gag Concert. Qua t&igrave;m kiếm, cảnh s&aacute;t ph&aacute;t hiện một thiết bị ghi h&igrave;nh được gắn trong nh&agrave; vệ sinh nữ của khu vực tổng duyệt của show h&agrave;i Gag Concert.</p>\n<p>Nhận được th&ocirc;ng tin, kh&aacute;n giả H&agrave;n Quốc tỏ ra tức giận v&agrave; lo lắng cho thần tượng của m&igrave;nh. Gag Concert vốn l&agrave; chương tr&igrave;nh h&agrave;i nổi tiếng nhất xứ kim chi với chỉ số rating cao. Kh&ocirc;ng chỉ c&aacute;c nghệ sĩ h&agrave;i, nhiều ca sĩ thần tượng, diễn vi&ecirc;n nữ cũng thường xuy&ecirc;n g&oacute;p mặt trong show với tư c&aacute;ch kh&aacute;ch mời.</p>\n<p>Hay gần nhất, ng&agrave;y 27/7, dư luận H&agrave;n Quốc lại một lần nữa dậy s&oacute;ng v&igrave; vụ việc nam ca sĩ Hwang Kyung Seok (nghệ danh The Film) thừa nhận đ&atilde; quay l&eacute;n cảnh khỏa th&acirc;n v&agrave; \"giường chiếu\" của nhiều phụ nữ. Theo c&aacute;o buộc của cảnh s&aacute;t, Hwang thậm ch&iacute; đ&atilde; ph&aacute;t t&aacute;n loạt video tr&ecirc;n l&ecirc;n c&aacute;c trang web khi&ecirc;u d&acirc;m nước ngo&agrave;i.</p>\n<p>\"Ph&iacute;a cảnh s&aacute;t cho biết c&oacute; khả năng Hwang Kyung Seok đ&atilde; tiếp cận người h&acirc;m mộ th&ocirc;ng qua phần tin nhắn ri&ecirc;ng tư tr&ecirc;n mạng x&atilde; hội, sau đ&oacute; dụ dỗ hoặc t&igrave;m c&aacute;ch b&iacute; mật đặt m&aacute;y quay l&eacute;n họ\", tờ Sports Chosun th&ocirc;ng tin th&ecirc;m về h&agrave;nh vi của Hwang Kyung Seok.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_21_2.jpg\" /></p>\n<p>... đến vấn nạn x&atilde; hội</p>\n<p>Tiếng H&agrave;n c&oacute; một từ l&oacute;ng để gọi camera quay l&eacute;n l&agrave; \"molka\". Khi t&igrave;m từ \"molka\" tr&ecirc;n cổng th&ocirc;ng tin Naver, c&oacute; tới hơn 85.000 kết quả trả về trong v&ograve;ng v&agrave;i gi&acirc;y v&agrave; đều c&oacute; nội dung phản &aacute;nh, ph&ecirc; ph&aacute;n vấn nạn quay l&eacute;n phụ nữ. Kh&ocirc;ng &iacute;t b&agrave;i trong số đ&oacute; cung cấp hẳn h&igrave;nh ảnh, video bằng chứng thu được từ camera tội phạm đặt ở nơi c&ocirc;ng cộng.</p>\n<p>C&aacute;c h&atilde;ng tin quốc tế như BBC, CNN hay những tờ b&aacute;o h&agrave;ng đầu H&agrave;n Quốc Yonhap News, Chosun Ilbo, Sports Chosun... đều từng đăng b&agrave;i b&oacute;c trần thủ đoạn của tội phạm quay l&eacute;n. C&aacute;c đối tượng n&agrave;y thường mua m&aacute;y quay nhỏ như con ch&iacute;p điện tử v&agrave; giấu trong ổ điện, m&aacute;y l&agrave;m kh&ocirc; tay, ph&iacute;a trong bồn cầu ở nh&agrave; vệ sinh c&ocirc;ng cộng hoặc dưới gầm b&agrave;n trong qu&aacute;n c&agrave; ph&ecirc;. Trong c&aacute;c nh&agrave; nghỉ, kh&aacute;ch sạn, loại m&aacute;y quay ẩn n&agrave;y c&oacute; thể được lắp đặt ở bất cứ đ&acirc;u, thậm ch&iacute; được giấu ở mặt trong gương soi.</p>\n<p>Với những chiếc m&aacute;y quay n&agrave;y, đa phần phụ nữ kh&ocirc;ng thể nhận ra bản th&acirc;n đang bị quay l&eacute;n trừ khi c&oacute; người kh&aacute;c v&ocirc; t&igrave;nh ph&aacute;t hiện ra. Đơn cử, trong vụ đặt m&aacute;y quay tại đ&agrave;i KBS, đạo diễn Gag Concert l&agrave; người nhận ra điểm bất thường ở nh&agrave; vệ sinh nữ v&agrave; đ&atilde; b&aacute;o cho cảnh s&aacute;t đến kiểm tra.</p>\n<p>Hay h&ocirc;m 21/7, nam diễn vi&ecirc;n Hậu duệ mặt trời Kim Min Seok v&ocirc; t&igrave;nh tr&ocirc;ng thấy một người đ&agrave;n &ocirc;ng đang d&ugrave;ng điện thoại quay l&eacute;n ph&iacute;a dưới v&aacute;y phụ nữ. Gần nhất, h&ocirc;m 25/7, khi tr&iacute;ch xuất camera an ninh của qu&aacute;n internet c&ocirc;ng cộng, ch&iacute;nh quyền ph&aacute;t hiện cảnh nữ MC bị người kh&aacute;c chụp l&eacute;n ph&iacute;a dưới v&aacute;y.</p>\n<p>Những video, h&igrave;nh ảnh chụp l&eacute;n n&agrave;y thường được chia sẻ l&ecirc;n c&aacute;c nền tảng mạng x&atilde; hội c&oacute; trả ph&iacute; - ch&iacute;nh l&agrave; xuất ph&aacute;t điểm của vụ \"ph&ograve;ng chat thứ N\" g&acirc;y chấn động dư luận H&agrave;n hồi th&aacute;ng 3, th&aacute;ng 4.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_21_3.jpg\" /></p>\n<p>n Quốc được coi l&agrave; một trong những quốc gia dẫn đầu về c&ocirc;ng nghệ số h&oacute;a, nhưng điều n&agrave;y lại v&ocirc; t&igrave;nh khiến vấn nạn quay l&eacute;n ở nước n&agrave;y ph&aacute;t triển mạnh mẽ hơn bất kỳ quốc gia n&agrave;o kh&aacute;c. Ai cũng c&oacute; thể ghi h&igrave;nh tr&aacute;i ph&eacute;p v&agrave; tung l&ecirc;n mạng x&atilde; hội m&agrave; kh&ocirc;ng gặp phải bất kỳ kh&oacute; khăn n&agrave;o. \"Một khi đ&atilde; được chia sẻ l&ecirc;n mạng, kh&oacute; l&ograve;ng x&oacute;a hết to&agrave;n bộ dấu vết về h&igrave;nh ảnh hay video nhạy cảm n&agrave;o đ&oacute;\", trang Hankuk Kyungje nhận định.</p>\n<p>Xem x&eacute;t tr&ecirc;n mức &aacute;n d&agrave;nh cho đối tượng quay l&eacute;n phụ nữ từng được c&ocirc;ng bố, dư luận H&agrave;n Quốc cho rằng luật ph&aacute;p vẫn c&ograve;n \"nhẹ tay\" với h&igrave;nh thức phạm tội n&agrave;y. Trong c&aacute;c b&agrave;i đăng tr&ecirc;n trang cộng đồng, d&acirc;n mạng xứ kim chi lu&ocirc;n bất b&igrave;nh v&igrave; những đối tượng quay l&eacute;n chỉ bị phạt h&agrave;nh ch&iacute;nh hoặc nhận &aacute;n t&ugrave; treo.</p>\n<p>\"Molka l&agrave; tội &aacute;c, đồng thời l&agrave; biểu hiện ti&ecirc;u biểu của h&agrave;nh vi bạo lực giới. Xử l&yacute; kh&ocirc;ng nghi&ecirc;m minh khiến tội phạm vẫn lu&ocirc;n b&igrave;nh thản trước nguy cơ bị cơ quan chức năng ph&aacute;t hiện\", một kh&aacute;n giả b&igrave;nh luận trong b&agrave;i viết về việc nữ MC mạng bị chụp l&eacute;n dưới v&aacute;y.</p>\n<p>Ch&iacute;nh phủ H&agrave;n Quốc v&agrave;o cuộc</p>\n<p>Trong loạt b&agrave;i viết về nạn quay l&eacute;n, truyền th&ocirc;ng H&agrave;n Quốc nhiều lần đề cập chuyện đặt camera ẩn tại c&aacute;c địa điểm như nh&agrave; vệ sinh, kh&aacute;ch sạn, qu&aacute;n c&agrave; ph&ecirc;... chỉ thực sự b&ugrave;ng nổ từ năm 2011.</p>\n<p>Th&aacute;ng 8/2018, phụ nữ H&agrave;n Quốc đ&atilde; tổ chức biểu t&igrave;nh tại quảng trường Gwanghwamun. Hơn 10.000 người mặc &aacute;o đỏ, ngồi dưới nắng n&oacute;ng v&agrave; giơ cao khẩu hiệu: \"My Life Is Not Your Porn\" (Cuộc đời t&ocirc;i kh&ocirc;ng phải phim khi&ecirc;u d&acirc;m của bạn).</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_21_4.jpg\" /></p>\n<p>\"Họ h&aacute;t theo ca kh&uacute;c trong vở nhạc kịch Những người khốn khổ. \'Ch&iacute;nh quyền c&oacute; nghe thấy kh&ocirc;ng? Đ&acirc;y l&agrave; tiếng h&aacute;t của những người phụ nữ đang giận dữ\'. Họ đại diện cho 70.000 phụ nữ tham gia v&agrave;o chiến dịch, phủ k&iacute;n quảng trường Gwanghwamun d&ugrave; đang trong thời kỳ nắng n&oacute;ng kỷ lục. Họ k&ecirc;u gọi ch&iacute;nh phủ nỗ lực đối ph&oacute; với tội &aacute;c quay l&eacute;n\", Korea Times viết. Đ&acirc;y cũng l&agrave; cuộc biểu t&igrave;nh lớn nhất chỉ d&agrave;nh cho phụ nữ từng xuất hiện trong lịch sử H&agrave;n Quốc.</p>\n<p>Theo Yonhap News, trong b&agrave;i ph&aacute;t biểu hồi th&aacute;ng 5/2018, ch&iacute;nh Tổng thống Moon Jae In từng thừa nhận vấn nạn quay l&eacute;n tồn tại như \"một phần của cuộc sống h&agrave;ng ng&agrave;y tại H&agrave;n Quốc\". &Ocirc;ng cho rằng ch&iacute;nh phủ cần tăng mức phạt v&agrave; phải nghi&ecirc;m khắc xử l&yacute; với đối tượng phạm tội quay trộm cảnh nhạy cảm.</p>\n<p>Trước khi &ocirc;ng Moon Jae In l&ecirc;n tiếng, giới chức H&agrave;n Quốc đ&atilde; c&oacute; những h&agrave;nh động thực tế nhằm giảm thiểu vấn nạn quay l&eacute;n. Chẳng hạn, v&agrave;o năm 2017, ch&iacute;nh quyền th&agrave;nh phố Seoul đ&atilde; ph&aacute;t hiện 6.500 trường hợp đặt m&aacute;y quay l&eacute;n th&acirc;n thể phụ nữ trong c&aacute;c khu vực như buồng thử đồ, nh&agrave; vệ sinh c&ocirc;ng cộng, t&agrave;u điện ngầm...</p>\n<p>Trang Irinsider đưa tin v&igrave; con số 6.500 trường hợp n&agrave;y, cơ quan chức năng từng phải thu&ecirc; tới 8.000 c&ocirc;ng nh&acirc;n để kiểm tra c&aacute;c nh&agrave; vệ sinh c&ocirc;ng cộng tại Seoul c&oacute; bị đặt m&aacute;y quay l&eacute;n hay kh&ocirc;ng.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_21_5.jpg\" /></p>\n<p>H&ocirc;m 24/6, một gi&aacute;o vi&ecirc;n trung học ở th&agrave;nh phố Gimhae (tỉnh Nam Gyeongsang) đ&atilde; bị bắt v&igrave; lắp camera trong ph&ograve;ng vệ sinh nữ nhằm chụp trộm học sinh v&agrave; gi&aacute;o vi&ecirc;n trong trường. C&ugrave;ng ng&agrave;y, th&ecirc;m một gi&aacute;o vi&ecirc;n kh&aacute;c tại Changnyeong cũng bị ph&aacute;t hiện c&oacute; h&agrave;nh động tương tự.</p>\n<p>Ngay lập tức, Li&ecirc;n đo&agrave;n gi&aacute;o vi&ecirc;n H&agrave;n Quốc v&agrave; Bộ Gi&aacute;o dục k&ecirc;u gọi ch&iacute;nh quyền mở cuộc điều tra, tiến h&agrave;nh điều tra để thu giữ c&aacute;c camera ẩn trong c&aacute;c trường học. Korea Times cho biết Bộ trưởng Gi&aacute;o dục Yoo Eun Hae gọi đ&acirc;y l&agrave; \"sự việc đ&aacute;ng xấu hổ\".</p>\n<p>\"T&ocirc;i lu&ocirc;n tự hỏi liệu c&oacute; bao nhi&ecirc;u tội phạm quay l&eacute;n chưa bị bắt ở ngo&agrave;i x&atilde; hội. Nhiều l&uacute;c t&ocirc;i cảm thấy mệt mỏi v&agrave; thất vọng v&igrave; chuyện n&agrave;y. C&oacute; phải chăng n&ecirc;n gọi l&agrave; lỗ hổng của ph&aacute;p luật\", một kh&aacute;n giả chia sẻ suy nghĩ trong b&agrave;i viết của tờ Chosun Ilbo h&ocirc;m 25/7.</p>',	'Giảng viên đại học David Tizzard từng nhận định nghệ sĩ nam tiêu khiển bằng các video bất hợp pháp. Việc trên không chỉ tồn tại trong showbiz mà còn là vấn nạn của xã hội Hàn Quốc.',	1,	2,	'2020-07-29 14:13:02',	NULL,	0,	3,	24),
(22,	'KAY TRẦN VÀ BÀI TOÁN \'GÀ ĐẺ TRỨNG VÀNG\' CỦA SƠN TÙNG M-TP',	'<p>&ldquo;Welcome aboard, singer Kay Trần! Exclusive artist of M-TP Talent&rdquo; (Tạm dịch: Ch&agrave;o đ&oacute;n gia nhập, ca sĩ Kay Trần. Nghệ sĩ độc quyền của M-TP Talent). D&ograve;ng trạng th&aacute;i ngắn ngủi tr&ecirc;n fanpage của Sơn T&ugrave;ng M-TP nhưng đủ th&ocirc;ng tin để x&aacute;c nhận Kay Trần ch&iacute;nh thức trở th&agrave;nh nghệ sĩ đầu ti&ecirc;n gia nhập m&ocirc; h&igrave;nh ph&aacute;t triển t&agrave;i năng trẻ m&agrave; giọng ca gốc Th&aacute;i B&igrave;nh mới x&acirc;y dựng.</p>\n<p>Sự c&ocirc;ng bố nhận được b&agrave;n t&aacute;n, nhất l&agrave; trong giới quan s&aacute;t m&ocirc; h&igrave;nh c&ocirc;ng ty quản l&yacute; v&agrave; &ldquo;g&agrave; chiến&rdquo;. C&acirc;u hỏi được đặt ra: Sơn T&ugrave;ng M-TP liệu sẽ l&agrave;m g&igrave; để Kay Trần, một gương mặt kh&ocirc;ng c&ograve;n thực sự mới mẻ, c&oacute; thể trở th&agrave;nh &ldquo;g&agrave; đẻ trứng v&agrave;ng&rdquo;, như v&iacute; von của thị trường?</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_22_1.jpg\" /></p>\n<p>Kay Trần c&oacute; g&igrave; trước khi đầu qu&acirc;n cho Sơn T&ugrave;ng M-TP?</p>\n<p>Kay Trần sinh năm 1994. Anh kh&ocirc;ng chỉ bằng tuổi, thậm ch&iacute; c&ograve;n hoạt động c&ugrave;ng thời với Sơn T&ugrave;ng M-TP. Kh&ocirc;ng c&oacute; nhiều dữ kiện để khẳng định t&igrave;nh bạn &ldquo;keo sơn gắn b&oacute;&rdquo; nhưng cả hai đều xuất th&acirc;n underground, từng sinh hoạt &acirc;m nhạc chung v&agrave; giữ mối quan hệ tốt.</p>\n<p>D&ugrave; vậy, Sơn T&ugrave;ng M-TP v&agrave; Kay Trần l&agrave; hai m&agrave;u sắc &acirc;m nhạc ri&ecirc;ng biệt v&agrave; cũng lựa chọn hướng đi ri&ecirc;ng trong ph&aacute;t triển sự nghiệp. Kay Trần được biết đến như ca sĩ, rapper. Anh cũng c&oacute; khả năng sản xuất &acirc;m nhạc d&ugrave; chưa nổi bật.</p>\n<p>Dấu ấn đ&aacute;ng kể đầu ti&ecirc;n của Kay Trần ở thị trường l&agrave; tham gia chương tr&igrave;nh Ng&ocirc;i sao Việt 2014. Ở thời điểm đ&oacute;, đ&acirc;y l&agrave; cuộc thi truyền h&igrave;nh thực tế đ&igrave;nh đ&aacute;m, ng&ocirc;i vị qu&aacute;n qu&acirc;n c&oacute; cơ hội được sang H&agrave;n Quốc đ&agrave;o tạo, tiền đề để ph&aacute;t triển sự nghiệp.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_22_2.jpg\" /></p>\n<p>Kay Trần l&agrave; gương mặt được ch&uacute; &yacute; năm đ&oacute; v&igrave; sở hữu hai &ldquo;vũ kh&iacute;&rdquo; rất H&agrave;n Quốc: Ngoại h&igrave;nh v&agrave; phong c&aacute;ch. Một gương mặt s&aacute;ng, nhảy đẹp v&agrave; giọng h&aacute;t cũng kh&ocirc;ng tệ.</p>\n<p>Đối thủ của Kay Trần tại Ng&ocirc;i sao Việt 2014 c&oacute; những gương mặt nặng k&yacute; kh&aacute;c như Soobin Ho&agrave;ng Sơn, Suni Hạ Linh, Liz Kim Cương v&agrave; T&ugrave;ng Mini. Chung cuộc, T&ugrave;ng Mini l&agrave; người chiến thắng, trong khi Kay Trần chọn c&aacute;ch dừng cuộc chơi trước đ&oacute;.</p>\n<p>Thời gian sau, Kay Trần c&oacute; một v&agrave;i sản phẩm, trong đ&oacute; đ&aacute;ng ch&uacute; &yacute; hơn cả l&agrave; m&agrave;n hợp t&aacute;c với Binz trong Ph&iacute;a sau em v&agrave;o năm 2015. Sản phẩm được một bộ phận kh&aacute;n giả đ&oacute;n nhận, đặc biệt l&agrave; giới mến mộ rap nhưng vẫn kh&ocirc;ng đủ tạo n&ecirc;n c&uacute; bứt ph&aacute; cho sự nghiệp của giọng ca sinh năm 1994.</p>\n<p>Nam rapper sau đ&oacute; c&oacute; thời gian &ldquo;im hơi lặng tiếng&rdquo; gần 3 năm trước khi trở lại ấn tượng v&agrave;o năm 2018 với nhiều thay đổi cả về h&igrave;nh ảnh lẫn &acirc;m nhạc.</p>\n<p>Anh c&oacute; loạt sản phẩm như &Yacute; em sao, Chuyện t&igrave;nh t&ocirc;i, Tết đong đầy&hellip; Th&agrave;nh quả cũng gi&uacute;p Kay Trần trở th&agrave;nh Nghệ sĩ Indie/Underground được y&ecirc;u th&iacute;ch nhất năm 2018, hạng mục nằm trong khu&ocirc;n khổ của Zing Music Awards 2018.</p>\n<p>Nhưng cũng trong giai đoạn n&agrave;y, nam ca sĩ g&acirc;y tranh c&atilde;i với MV V&ocirc; ra ra v&ocirc;. Sản phẩm c&oacute; nhiều cảnh quay bị nhận x&eacute;t l&agrave; dung tục, phản cảm trong m&ocirc; tả l&agrave;m t&igrave;nh. Đ&aacute;ng n&oacute;i, &acirc;m nhạc của MV cũng kh&ocirc;ng g&acirc;y ấn tượng. Sản phẩm đồng thời thất bại cả về lượt xem, bị truyền th&ocirc;ng thờ ơ.</p>\n<p>Sau những khen ch&ecirc; cho năm 2018, Kay Trần c&oacute; năm 2019 im ắng hơn. Anh g&oacute;p giọng với Han Sara trong MV Đếm cừu. Sự ăn &yacute; của cả hai trong hợp t&aacute;c đ&atilde; đưa Đếm cừu thuộc nh&oacute;m những MV được y&ecirc;u th&iacute;ch, c&oacute; lượt xem/nghe cao năm vừa qua.</p>\n<p>Những c&uacute; hợp t&aacute;c kh&aacute;c nhau v&agrave; loạt sản phẩm cho thấy Kay Trần l&agrave; gương mặt đa dạng về m&agrave;u sắc &acirc;m nhạc. Tuy nhi&ecirc;n, những thăng trầm trong qu&atilde;ng thời gian theo nghề cũng chứng minh nam ca sĩ thiếu chiến lược đường d&agrave;i v&agrave; kh&ocirc;ng c&oacute; khả năng tự x&aacute;c lập phong c&aacute;ch cụ thể để theo đuổi.</p>\n<p>V&agrave;, đ&oacute; cũng l&agrave; b&agrave;i to&aacute;n đầy th&aacute;ch thức m&agrave; Sơn T&ugrave;ng M-TP cần giải khi chọn nam ca sĩ l&agrave; &ldquo;g&agrave; chiến đầu ti&ecirc;n&rdquo;.</p>\n<p>Bằng c&aacute;ch n&agrave;o để &ldquo;g&agrave; đẻ trứng v&agrave;ng&rdquo;?</p>\n<p>Sơn T&ugrave;ng M-TP mới đ&acirc;y chia sẻ th&ocirc;ng tin c&oacute; thể sẽ ngừng đi h&aacute;t sau năm 30 tuổi, thay v&agrave;o đ&oacute;, anh d&agrave;nh nhiều thời gian cho gia đ&igrave;nh. Về &acirc;m nhạc, nam ca sĩ c&oacute; hướng x&acirc;y dựng m&ocirc;i trường để ph&aacute;t triển v&agrave; n&acirc;ng đỡ những t&agrave;i năng trẻ.</p>\n<p>Việc giọng ca C&oacute; chắc y&ecirc;u l&agrave; đ&acirc;y mở nh&aacute;nh M-TP Talent nằm trong c&ocirc;ng ty ri&ecirc;ng được cho l&agrave; bước đầu của kế hoạch d&agrave;i hạn m&agrave; anh tiết lộ.</p>\n<p>Thực tế, m&ocirc; h&igrave;nh ph&aacute;t triển t&agrave;i năng của c&aacute;c c&ocirc;ng ty giải tr&iacute; được đ&aacute;nh gi&aacute; l&agrave; đ&oacute;ng g&oacute;p kh&ocirc;ng nhỏ cho c&ocirc;ng nghiệp &acirc;m nhạc. C&acirc;u chuyện của thị trường Kpop l&agrave; dẫn chứng điển h&igrave;nh.</p>\n<p>Từ cuối thập ni&ecirc;n 2000 đến nay, c&aacute;c c&ocirc;ng ty giải tr&iacute; của H&agrave;n Quốc mọc l&ecirc;n như nấm, đầu tư kh&ocirc;ng &iacute;t tiền của cho &ldquo;talent&rdquo; trong chiến lược &ldquo;nu&ocirc;i g&agrave; đẻ trứng v&agrave;ng&rdquo;.</p>\n<p>C&acirc;u chuyện th&agrave;nh c&ocirc;ng ở thị trường H&agrave;n Quốc, đ&ocirc;i khi được quyết định từ mức độ đầu tư của c&ocirc;ng ty giải tr&iacute; hơn l&agrave; t&agrave;i năng thực sự của c&aacute;c &ldquo;talent&rdquo;, đặc biệt l&agrave; trong m&ocirc; h&igrave;nh nh&oacute;m nhạc.</p>\n<p>Thị trường nhạc Việt hiện nay cũng c&oacute; kh&ocirc;ng &iacute;t c&ocirc;ng ty giải tr&iacute; theo đuổi m&ocirc; h&igrave;nh của H&agrave;n Quốc d&ugrave; c&ograve;n nhiều ch&ecirc;nh lệch. Song, thực tế, cũng đ&atilde; c&oacute; những c&aacute; nh&acirc;n nổi bật nhờ chiến lược b&agrave;i bản v&agrave; sự đầu tư của c&ocirc;ng ty, b&ecirc;n cạnh khả năng của ca sĩ. Th&agrave;nh c&ocirc;ng của AMEE từ ST.319 mới đ&acirc;y l&agrave; v&iacute; dụ ti&ecirc;u biểu.</p>\n<p>Song, AMEE cũng l&agrave; điển h&igrave;nh của quy tr&igrave;nh ph&aacute;t hiện v&agrave; định danh \"talent\". 15 tuổi v&agrave; gần như chưa hề được biết đến rộng r&atilde;i, giọng ca 10X đầu qu&acirc;n cho ST.319, được r&egrave;n luyện, đ&agrave;o tạo về &acirc;m nhạc, giải tr&iacute; trước khi \"ra trận\" với loạt sản phẩm li&ecirc;n tiếp. Album đầu tay vừa ra mắt cũng l&agrave; khi c&ocirc; tr&ograve;n 20 tuổi.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_22_3.jpg\" /></p>\n<p>Song, trường hợp của Kay Trần với c&ocirc;ng ty của Sơn T&ugrave;ng M-TP l&agrave; th&aacute;ch thức kh&ocirc;ng nhỏ khi bản th&acirc;n nam rapper kh&ocirc;ng phải &ldquo;t&acirc;n binh&rdquo;. Ở tuổi 26, anh cũng kh&ocirc;ng c&ograve;n qu&aacute; trẻ so với mặt bằng chung của thị trường.</p>\n<p>Một \"talent\" đ&atilde; được biết đến nhưng chưa thật th&agrave;nh c&ocirc;ng thường đưa đến hai lựa chọn cho c&ocirc;ng ty quản l&yacute;: &ldquo;đập đi x&acirc;y lại&rdquo;, tức x&acirc;y dựng h&igrave;nh ảnh v&agrave; &acirc;m nhạc ho&agrave;n to&agrave;n kh&aacute;c so với trước, hoặc tiếp tục ph&aacute;t triển phong c&aacute;ch vốn c&oacute; nhưng đầu tư b&agrave;i bản hơn.</p>\n<p>Sơn T&ugrave;ng M-TP hiện chưa tiết lộ cụ thể sẽ l&agrave;m g&igrave; cho Kay Trần. Trong khi Kay Trần cũng chỉ mới x&aacute;c nhận việc đầu qu&acirc;n cho c&ocirc;ng ty giải tr&iacute;, chấm dứt con đường tự lập từng theo đuổi. Ngo&agrave;i ra, nam ca sĩ chưa c&ocirc;ng bố bất cứ dự định d&agrave;i hạn n&agrave;o nhằm tạo bước tiến trong nghề.</p>\n<p>Trực thuộc một c&ocirc;ng ty giải tr&iacute; c&oacute; trụ sở ở TP.HCM, một nh&agrave; sản xuất n&oacute;i Zing: &ldquo;Đầu qu&acirc;n về với Sơn T&ugrave;ng M-TP l&agrave; lựa chọn th&ocirc;ng minh của Kay Trần. Nhưng với Sơn T&ugrave;ng, Kay Trần l&agrave; th&aacute;ch thức thấy r&otilde;. Vẽ l&ecirc;n tờ giấy đ&atilde; c&oacute; m&agrave;u bao giờ cũng kh&oacute; hơn một tờ giấy trắng&rdquo;.</p>',	'Sơn Tùng M-TP vừa công bố hai “talent” gia nhập công ty, Kay Trần và Onionn. Trong đó, Kay Trần là lựa chọn gây bất ngờ, đồng thời cũng đặt thắc mắc về chiến lược của Sơn Tùng.',	0,	2,	'2020-07-29 14:15:20',	NULL,	0,	3,	25),
(23,	'10 ngôi sao có khí chất của màn ảnh Hàn',	'<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_23_1.jpg\" /></p>\n<p>Jin (BTS): Theo Koreaboo, trang Exciting DC vừa tổ chức một cuộc b&igrave;nh chọn người nổi tiếng c&oacute; thần th&aacute;i sang chảnh, đẳng cấp nhất, thu h&uacute;t khoảng 50.000 người tham gia. Kết quả, th&agrave;nh vi&ecirc;n Jin (Kim Seok Jin) của nh&oacute;m nhạc nổi tiếng BTS đứng đầu bảng xếp hạng, chiếm 28% số phiếu b&igrave;nh chọn. Nam thần tượng nhiều lần g&acirc;y ch&uacute; &yacute; tại thảm đỏ sự kiện bởi anh sở hữu v&oacute;c d&aacute;ng cao r&aacute;o, gương mặt sắc n&eacute;t v&agrave; phong th&aacute;i nổi bật.</p>\n<p>Jeon Ji Hyun: &ldquo;Mợ chảnh&rdquo; xếp thứ 2 trong danh s&aacute;ch n&oacute;i tr&ecirc;n với 23% phiếu bầu. Jeon Ji Hyun theo đuổi gu thời trang thanh lịch, sang trọng, thường xuy&ecirc;n mặc đồ hiệu đắt tiền. Kh&iacute; chất của nữ diễn vi&ecirc;n ph&ugrave; hợp với tinh thần của nhiều nh&atilde;n h&agrave;ng thời trang, trang sức cao cấp.</p>\n<p>Seo Kang Joon: Đứng thứ 3 trong danh s&aacute;ch l&agrave; &ldquo;mỹ nam mắt n&acirc;u quyến rũ&rdquo; của H&agrave;n Quốc - Seo Kang Joon. Anh về đ&iacute;ch với 22% phiếu bầu. T&agrave;i tử 27 tuổi được đ&aacute;nh gi&aacute; cao về ngoại h&igrave;nh hơn l&agrave; t&agrave;i năng. Seo Kang Joon sở hữu gương mặt g&oacute;c cạnh v&agrave; đ&ocirc;i mắt n&acirc;u, dễ d&agrave;ng thu h&uacute;t &aacute;nh nh&igrave;n của người đối diện.</p>\n<p>Song Hye Kyo: Ngọc nữ họ Song lọt bảng xếp hạng với vị tr&iacute; số 4 nhờ chiếm 9% phiếu bầu. Nữ diễn vi&ecirc;n lu&ocirc;n g&acirc;y ấn tượng bằng vẻ đẹp tự nhi&ecirc;n, trẻ trung hơn tuổi 39. Song Hye Kyo thường xuy&ecirc;n được mời chụp họa b&aacute;o, dự sự kiện lớn trong v&agrave; ngo&agrave;i nước, kho&aacute;c l&ecirc;n m&igrave;nh những bộ c&aacute;nh v&agrave; trang sức đắt đỏ.</p>\n<p>Hyun Bin: Xếp sau Song Hye Kyo l&agrave; t&agrave;i tử phim Hạ c&aacute;nh nơi anh. Hyun Bin gi&agrave;nh được 7% phiếu bầu. Nam diễn vi&ecirc;n nhiều lần l&agrave;m kh&aacute;ch mời danh dự tại c&aacute;c show diễn thời trang lớn ở Italy, Ph&aacute;p&hellip; Gu ăn mặc đời thường của Hyun Bin tuy đơn giản nhưng đem tới cảm gi&aacute;c lịch l&atilde;m, đẳng cấp.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_23_2.jpg\" /></p>\n<p>Tzuyu (Twice): Koreaboo nhận x&eacute;t rằng ở em &uacute;t của nh&oacute;m Twice to&aacute;t ra kh&iacute; chất tiểu thư sang chảnh. Điều n&agrave;y c&oacute; lẽ xuất ph&aacute;t từ gia thế gi&agrave;u c&oacute; của Tzuyu. Bố mẹ của c&ocirc; th&agrave;nh c&ocirc;ng trong việc điều h&agrave;nh chuỗi thẩm mỹ viện c&oacute; chi nh&aacute;nh tại Trung Quốc v&agrave; kinh doanh cửa h&agrave;ng c&agrave; ph&ecirc;. Trong danh s&aacute;ch tr&ecirc;n, Tzuyu đạt được 5% phiếu bầu.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/public/images/post_23_3.jpg\" /></p>\n<p>Irene (Red Velvet): 4% phiếu bầu l&agrave; th&agrave;nh t&iacute;ch m&agrave; chị cả của nh&oacute;m Red Velvet đạt được trong cuộc b&igrave;nh chọn n&oacute;i tr&ecirc;n. Irene l&agrave; một trong những nữ thần tượng c&oacute; nhan sắc nổi bật nhất gen 3 (thế hệ thứ 3) của Kpop. C&ocirc; được nhiều thương hiệu thời trang, trang sức xa xỉ ưu &aacute;i, chọn l&agrave;m n&agrave;ng thơ, cũng l&agrave; kh&aacute;ch mời quen thuộc tại show diễn của nhiều nh&agrave; mốt danh tiếng tr&ecirc;n thế giới.</p>\n<p>Yoona (SNSD): Giống như Irene, Yoona cũng được mệnh danh l&agrave; nữ thần sắc đẹp của Kpop, thuộc gen 2 (thế hệ thứ 2). Mỹ nh&acirc;n 30 tuổi gi&agrave;nh được 2% phiếu bầu. Với nhan sắc thanh thuần, trong trẻo, gu ăn mặc thanh lịch, sang trọng, Yoona nhận được nhiều hợp đồng quảng c&aacute;o c&oacute; gi&aacute; trị cho c&aacute;c nh&atilde;n h&agrave;ng mỹ phẩm, thời trang v&agrave; trang sức nổi tiếng.</p>\n<p>Kim Soo Hyun: Kh&aacute;c với h&igrave;nh ảnh c&oacute; phần t&ugrave;y &yacute;, lối ăn mặc đơn điệu trong bộ phim đang ph&aacute;t s&oacute;ng It&rsquo;s Okay to Not Be Okay (Đi&ecirc;n th&igrave; c&oacute; sao), ngo&agrave;i đời, Kim Soo Hyun được đ&aacute;nh gi&aacute; l&agrave; c&oacute; phong th&aacute;i sang trọng, đẳng cấp. Anh xếp thứ 9 trong danh s&aacute;ch với 1% phiếu bầu.</p>\n<p><img src=\"/public/images/post_23_4.jpg\" /></p>\n<p>Kim Sa Rang: Cựu Hoa hậu, diễn vi&ecirc;n Kim Sa Rang l&agrave; người \"chốt sổ\" top 10 với 1% phiếu bầu. C&ocirc; sở hữu gương mặt đẹp, v&oacute;c d&aacute;ng gợi cảm.</p>',	'Jin (BTS), Song Hye Kyo, Kim Soo Hyun vừa lọt top 10 người nổi tiếng có thần thái sang trọng, đẳng cấp do fan bình chọn.',	1,	2,	'2020-07-29 14:18:17',	NULL,	0,	3,	26);
